import echarts from 'echarts';
import { headerApi } from '@/api/index';
export default {
    data () {
        return {
            infoList: [],
            grid: {
                xl: 6,
                lg: 12,
                md: 12,
                sm: 12,
                xs: 24
            },
            excessStyle: {
                color: '#f56a00',
                backgroundColor: '#fde3cf'
            },
            avatarList: []
        }
    },
    methods: {
        // 统计
        getStatistics () {
            headerApi().then(async res => {
                let data = res.data
                this.infoList = data.info;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        }
    },
    mounted () {
        this.getStatistics();
    }
}
