import {storeListApi, storeShareApi} from '@/api/store'
// import {
// 	putRemarkData, putRechargeRemarkData, putVipRemarkData
// } from '@/api/store';
export default {
	name: 'distshow',
	data() {
		return {
			id: 0,
			currentid: 0,
			modals: false,
			loading: false,
			orderList: [],
			total: 0,
			formValidate: {
			  page: 1,
			  limit: 15,
			  keywords:'',
			  id:0
			},
			columns: [
				{
				  title: "选择",
				  // key: "chose",
				  width: 60,
				  align: "center",
				  render: (h, params) => {
				    let uid = params.row.id;
				    let flag = false;
					// this.currentid === uid
				    if (this.currentid === uid) {
				      flag = true;
				    } else {
				      flag = false;
				    }
				    let self = this;
				    return h("div", [
				      h("Radio", {
				        props: {
				          value: flag,
				        },
				        on: {
				          "on-change": () => {
							  self.currentid = uid
							  storeShareApi({oid:this.id,store_id:params.row.id}).then(res=>{
								  this.$Message.success(res.msg)
								  this.modals = false
								  self.currentid = 0
								  this.$parent.getList()
									this.$parent.closeDetail();
							  }).catch(err=>{
								  this.modals = false
								  this.$Message.error(err.msg)
								  self.currentid = 0
							  })
				          },
				        },
				      }),
				    ]);
				  },
				},
				{
					title: 'ID',
					key: 'id',
					minWidth: 50
				},
				{
					title: '门店图片',
					slot: 'image',
					minWidth: 80
				},
				{
					title: '门店名称',
					key: 'name',
					minWidth: 80
				},
				{
					title: '联系电话',
					key: 'phone',
					minWidth: 80
				},
				{
					title: '门店地址',
					key: 'address',
					ellipsis: true,  
					minWidth: 150
				},
				{
					title: '营业时间',
					key: 'day_time',
					minWidth: 120
				},
			    {
			        title: '营业状态',
			        key: 'status_name',
			        minWidth: 80
			    }]

		}
	},
	props: {},
	mounted() {
		// this.getList()
	},
	methods: {
		userSearchs(){
			this.formValidate.page = 1;
			this.getList(this.id);
		},
		getList(id){
			this.id = id
			this.formValidate.id = id;
			this.loading = true
			storeListApi(this.formValidate).then(res=>{
				this.orderList = res.data.list
				this.total = res.data.count
				this.loading = false
			})
		},
		cancel(name) {
			this.modals = false;
		},
		putRemark() {

		},
		//分页
		pageChange(status) {
		  this.formValidate.page = status;
		  this.getList(this.id)
		}
	}
}
