.contentBox {
  height: 100%;
  width: 100%;
  position: static;

  #content {
    position: absolute;
    /* top: 280px; */
    bottom: 0;
    width: 86%;
    /* height 1000px; */
  }
}

.contentBox >>> .vue-waterfall-easy {
  width: 100% !important;
  left: 0 !important;
  margin-left: 0 !important;
}

.contentBox >>> .vue-waterfall-easy-scroll::-webkit-scrollbar {
  display: none;
}

.contentBox >>> .vue-waterfall-easy-scroll {
  scrollbar-width: none; /* firefox */
  -ms-overflow-style: none; /* IE 10+ */
  overflow-x: hidden;
  overflow-y: auto;
}

.some-info {
  padding: 7px;
  box-sizing: border-box;
  text-align: center;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.Refresh {
  font-size: 12px;
  color: #1890FF;
  cursor: pointer;
  line-height: 35px;
  display: inline-block;
}

.news_pic {
  width: 100%;
  height: 150px;
  overflow: hidden;
  position: relative;
  background-size: 100%;
  background-position: center center;
  border-radius: 5px 5px 0 0;
  padding: 10px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
}

.news_sp {
  font-size: 12px;
  color: #000000;
  background: #fff;
  width: 100%;
  height: 38px;
  line-height: 38px;
  padding: 0 12px;
  box-sizing: border-box;
  display: block;
}

.news_cent {
  width: 100%;
  height: auto;
  background: #fff;
  border-top: 1px dashed #eee;
  display: flex;
  padding: 10px;
  box-sizing: border-box;
  justify-content: space-between;

  .news_sp1 {
    font-size: 12px;
    color: #000000;
    width: 71%;
  }

  .news_cent_img {
    width: 81px;
    height: 46px;
    border-radius: 6px;
    overflow: hidden;

    img {
      width: 100%;
      height: 100%;
    }
  }
}

.news_pic >>> .ivu-btn-error {
  width: 24px !important;
  height: 24px !important;
  background: #FF5D5F !important;
  color: #fff !important;
  border: 1px solid #eee !important;
}

.news_pic >>>.ivu-btn-error:hover {
  background: #FF5D5F !important;
  border: 1px solid #fff !important;
  color: #fff !important;
}

.news_pic >>> .ivu-btn-success {
  width: 24px !important;
  height: 24px !important;
  border: 1px solid #eee !important;
}
