	import { mapState } from "vuex";
	import timeOptions from "@/utils//timeOptions";
	import {getGroupTemplateList,workGroupTemplateSendMsg} from "@/api/work";
	export default {
		data() {
			return {
				options: timeOptions,
				timeVal: [],
				tableFrom: {
					name:"",
					time:"",
					type:0,
					client_type:"",
					page: 1,
					limit: 15,
				},
				total: 0,
				tableList: [],
				loading: false,
				tableData: [],
				columns1: [
                    {
                        title: '群发名称',
                        key: 'name',
                        minWidth: 150
                    },
                    {
                        title: '已发送成员',
                        key: 'user_count',
                        minWidth: 120
                    },
                    {
                        title: '送达客户',
                        key: 'external_user_count',
                        minWidth: 120
                    },
                    {
                        title: '未发送成员',
                        key: 'unuser_count',
                        minWidth: 120
                    },
                    {
                        title: '未送达客户',
                        key: 'external_unuser_count',
                        minWidth: 120
                    },
					{
                        title: '是否发送',
                        slot: 'send_type',
                        minWidth: 120
                    },
					{
                        title: '群发类型',
                        slot: 'template_type',
                        minWidth: 120
                    },
					{
                        title: '发送时间',
                        key: 'update_time',
                        minWidth: 150
                    },
					{
                        title: '创建时间',
                        key: 'create_time',
                        minWidth: 150
                    },
                    {
                        title: '操作',
                        slot: 'action',
                        fixed: 'right',
                        minWidth: 170
                    }
                ],

			}
		},
		computed: {
		  ...mapState("admin/layout", ["isMobile"]),
		  labelWidth() {
		    return this.isMobile ? undefined : 96;
		  },
		  labelPosition() {
		    return this.isMobile ? "top" : "right";
		  }
		},
		created() {
			this.getList();
		},
		methods: {
			getList(){
				this.loading = true;
				getGroupTemplateList(this.tableFrom).then(res=>{
					this.tableData = res.data;
					this.loading = false;
				}).catch(err=>{
					this.$Message.error(err.msg);
					this.loading = false;
				})
			},
			search(){
				this.tableFrom.page = 1;
				this.getList();
			},
			// 具体日期
            onchangeTime (e) {
                this.timeVal = e;
				this.tableFrom.time = this.timeVal.join("-");
            },
			pageChange(index){
				this.tableFrom.page = index;
				this.getList();
			},
			// 删除
			delItem(row,index){
				let delfromData = {
					title: '删除该客户群发',
					num:index,
					url: `work/group_template/${row.id}`,
					method: "DELETE",
					ids: "",
				};
				this.$modalSure(delfromData)
					.then((res) => {
					this.$Message.success(res.msg);
					if(this.tableData.list.length == 1){
						this.tableFrom.page = this.tableFrom.page -1;
						this.getList();
					}else{
						this.getList();
					}
					})
					.catch((res) => {
					this.$Message.error(res.msg);
				});
			},
			detailsItem(row,index){
				this.$router.push("/admin/work/client/group_info/" + row.id);
			},
			sendMsg(row,index){
				workGroupTemplateSendMsg({
					userid:"",
					time:row.update_time,
					id:row.id
				}).then(res=>{
					this.$Message.success(res.msg)
				}).catch(err=>{
					this.$Message.error(err.msg);
				})
			}
		}
	}
