import { mapState } from "vuex";
export default {
  name: "banner", // 组件名称
  cname: "轮播图", // 标题名称
  icon: "icontupianguanggao1",
  defaultName: "swiperBg", // 外面匹配名称
  configName: "c_banner", // 右侧配置名称
  type: 0, // 0 基础组件 1 营销组件 2工具组件
  props: {
    index: {
      type: null,
    },
    num: {
      type: null,
    },
  },
  computed: {
    ...mapState("admin/mobildConfig", ["defaultArray"]),
  },
  watch: {
    pageData: {
      handler(nVal, oVal) {
        this.setConfig(nVal);
      },
      deep: true,
    },
    num: {
      handler(nVal, oVal) {
        let data = this.$store.state.admin.mobildConfig.defaultArray[nVal];
        this.setConfig(data);
      },
      deep: true,
    },
    defaultArray: {
      handler(nVal, oVal) {
        let data = this.$store.state.admin.mobildConfig.defaultArray[this.num];
        this.setConfig(data);
      },
      deep: true,
    },
  },
  data() {
    return {
      // 默认初始化数据禁止修改
      defaultConfig: {
        name: "swiperBg",
        timestamp: this.num,
        setUp: {
          tabVal: 0
        },
        // 模板选择
        // tabConfig: {
        //   tabVal: 0,
        //   type: 1,
        //   tabList: [
        //     {
        //       name: "单图模板",
        //       icon: "iconbanner_1",
        //     },
        //     {
        //       name: "多图模板1",
        //       icon: "iconbanner_2",
        //     },
        //     {
        //       name: "多图模板2",
        //       icon: "iconbanner_3",
        //     },
        //   ],
        // },
        // 图片列表
        swiperConfig: {
          title:
            "最多可添加10张图片，建议宽度750px；鼠标拖拽左侧圆点可调整图片 顺序",
          maxList: 10,
          list: [
            {
              img: "",
              info: [
                {
                  title: "标题",
                  value: "今日推荐",
                  tips: "选填，不超过4个字",
                  max: 4,
                },
                {
                  title: "链接",
                  value: "",
                  tips: "请输入链接",
                  max: 100,
                },
              ],
            },
          ],
        },
        isShow: {
          title: '是否显示背景色',
          val: true
        },
        // 背景颜色
        bgColor: {
          title: "背景颜色(渐变)",
          default: [
            {
              item: "#F62C2C",
            },
            {
              item: "#F96E29",
            },
          ],
          color: [
            {
              item: "#F62C2C",
            },
            {
              item: "#F96E29",
            },
          ],
        },
        dotColor: {
          title: "指示器颜色",
          default: [
            {
              item: "#fff",
            },
          ],
          color: [
            {
              item: "#fff",
            },
          ],
        },
        // 左右间距
        lrConfig: {
          title: "左右边距",
          val: 10,
          min: 0,
        },
        // 页面间距
        mbConfig: {
          title: "页面间距",
          val: 0,
          min: 0,
        },
        // 轮播图点样式
        docConfig: {
          cname: "swiper",
          title: "指示器样式",
          type: 0,
          list: [
            {
              val: "圆形",
              icon: "iconDot",
            },
            {
              val: "直线",
              icon: "iconSquarepoint",
            },
            {
              val: "数字",
              icon: "iconshuzi",
            },
            {
              val: "无指示器",
              icon: "iconjinyong",
            },
          ],
        },
        txtStyle: {
          title: '指示器位置',
          type: 0,
          list: [
            {
              val: '居左',
              icon:'icondoc_left'
            },
            {
              val: '居中',
              icon:'icondoc_center'
            },
            {
              val: '居右',
              icon:'icondoc_right'
            }
          ]
        },
        // 图片样式
        imgConfig: {
          cname: "docStyle",
          title: "轮播图样式",
          type: 0,
          list: [
            {
              val: "圆角",
              icon: "iconPic_fillet",
            },
            {
              val: "直角",
              icon: "iconPic_square",
            },
          ],
        },
      },
      pageData: {},
      bgColor: [],
      mTOP: 0,
      edge: 0,
      imgStyle: 0,
      imgSrc: "",
      docStyle: 0,
      dotPosition: 0,
      dotColor: "",
      isShow:true,
    };
  },
  mounted() {
    this.$nextTick(() => {
      this.pageData = this.$store.state.admin.mobildConfig.defaultArray[
        this.num
      ];
      this.setConfig(this.pageData);
    });
  },
  methods: {
    setConfig(data) {
      if (!data) return;
      if(data.mbConfig){
        this.isShow = data.isShow.val;
        this.bgColor = data.bgColor.color;
        this.mTOP = data.mbConfig.val;
        this.edge = data.lrConfig.val;
        this.imgStyle = data.imgConfig.type;
        this.imgSrc = data.swiperConfig.list.length
                ? data.swiperConfig.list[0].img
                : "";
        this.docStyle = data.docConfig.type;
        this.dotPosition = data.txtStyle.type;
        this.dotColor = data.dotColor.color[0].item;
      }
    },
  },
};
