import { mapState } from 'vuex'
export default {
    name: 'home_product',
    cname: '促销列表',
    configName: 'c_home_product',
    icon: 'iconcuxiaoliebiao1',
    type: 0, // 0 基础组件 1 营销组件 2工具组件
    defaultName: 'promotionList', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }

    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'promotionList',
                timestamp: this.num,
                setUp: {
                    tabVal: 0
                },
                productList: {
                    title:'促销列表',
                    list:[]
                },
                titleConfig: {
                    title: '标题位置',
                    type: 0,
                    list: [
                        {
                            val: '居左',
                            icon:'icondoc_left'
                        },
                        {
                            val: '居中',
                            icon:'icondoc_center'
                        },
                        {
                            val: '居右',
                            icon:'icondoc_right'
                        }
                    ]
                },
                titleShow: {
                    title: '是否显示商品名称',
                    val: true
                },
                opriceShow: {
                    title: '是否显示商品原价',
                    val: true
                },
                priceShow: {
                    title: '是否显示商品价格',
                    val: true
                },
                couponShow: {
                    title: '是否显示优惠券',
                    val: true
                },
                tabConfig: {
                    title: '最多可添加4个版块；鼠标拖拽左侧圆点可调整版块顺序',
                    max: 4,
                    tabCur: 0,
                    list: [
                        {
                            chiild: [
                                {
                                    title: '标题',
                                    val: '首发新品',
                                    max: 4,
                                    pla: '选填，不超过四个字'
                                },
                                {
                                    title: '简介',
                                    val: '最新出炉',
                                    max: 4,
                                    pla: '选填，不超过四个字'
                                }
                            ],
                            link: {
                                title: '链接',
                                activeVal: 0,
                                optiops: [
                                    {
                                        type: 0,
                                        value: 1,
                                        label: '精品推荐'
                                    },
                                    {
                                        type: 1,
                                        value: 2,
                                        label: '热门榜单'
                                    },
                                    {
                                        type: 2,
                                        value: 3,
                                        label: '首发新品'
                                    },
                                    {
                                        type: 3,
                                        value: 4,
                                        label: '促销单品'
                                    }
                                ]
                            }
                        }
                    ]
                },
                themeColor: {
                    title: '主题风格',
                    name: 'themeColor',
                    default: [{
                        item: '#F95429'
                    }],
                    color: [
                        {
                            item: '#F95429'
                        }
                    ]
                },
                fontColor: {
                    title: '价格颜色',
                    name: 'fontColor',
                    default: [{
                        item: '#e93323'
                    }],
                    color: [
                        {
                            item: '#e93323'
                        }
                    ]
                },
                labelColor: {
                    title: '活动标签',
                    name: 'labelColor',
                    default: [{
                        item: '#e93323'
                    }],
                    color: [
                        {
                            item: '#e93323'
                        }
                    ]
                },
                // 页面间距
                mbConfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                },
                numConfig: {
                    val: 6
                }
            },
            navlist: [],
            imgStyle: '',
            txtColor: '',
            slider: '',
            tabCur: 0,
            list: [],
            activeColor: '',
            fontColor: '',
            labelColor:'',
            pageData: {},
            titleConfig: 0,
            titleShow: true,
            opriceShow: true,
            priceShow: true,
            couponShow: true
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbConfig){
                this.navlist = data.tabConfig.list;
                // this.imgStyle = data.imgStyle.type
                this.activeColor = data.themeColor.color[0].item;
                this.fontColor = data.fontColor.color[0].item;
                this.labelColor = data.labelColor.color[0].item;
                this.slider = data.mbConfig.val;
                this.titleConfig = data.titleConfig.type;
                this.titleShow = data.titleShow.val;
                this.opriceShow = data.opriceShow.val;
                this.priceShow = data.priceShow.val;
                this.couponShow = data.couponShow.val;
                this.tabCur = data.tabConfig.tabCur || 0;
                let productList = data.productList.list || [];
                if(productList.length){
                    this.list = productList;
                }else {
                    this.list = [
                        {
                            image: '',
                            store_name: '小米便携式蓝牙音响',
                            price: '59',
                            ot_price: 135,
                            checkCoupon: true,
                            activity: {type: "2", id: 5}
                        },
                        {
                            image: '',
                            store_name: '小米便携式蓝牙音响',
                            price: '59',
                            ot_price: 135,
                            checkCoupon: true,
                            activity: []
                        }
                    ];
                }
            }
        }
    }
}
