import vuedraggable from 'vuedraggable'
import uploadPictures from '@/components/uploadPictures';
import linkaddress from '@/components/linkaddress';
export default {
    name: 'uploadPic',
    props: {
        listData: {
            type: Array
        },
        type: {
            type: Number
        }
    },
    components: {
        draggable: vuedraggable,
        uploadPictures,
        linkaddress
    },
    data () {
        return {
            modalPic: false,
            isChoice: '单选',
            gridBtn: {
                xl: 4,
                lg: 8,
                md: 8,
                sm: 8,
                xs: 8
            },
            gridPic: {
                xl: 6,
                lg: 8,
                md: 12,
                sm: 12,
                xs: 12
            },
            activeIndex: 0,
            lastObj: {
                name:'',
                pic:'',
                url:''
            }
        }
    },
    mounted () {
    },
    watch: {
        configObj: {
            handler (nVal, oVal) {
            },
            deep: true
        }
    },
    methods: {
        linkUrl(e){
            this.listData[this.activeIndex].url = e
        },
        getLink (index){
            this.activeIndex = index
            this.$refs.linkaddres.modals = true
        },
        addBox () {
            if (this.listData.length == 0) {
                this.listData.push(this.lastObj)
            } else {
                let obj = JSON.parse(JSON.stringify(this.listData[this.listData.length - 1]))
                obj.name = '';
                obj.pic = '';
                obj.url = '';
                this.listData.push(obj)
            }
            // this.$emit('parentFun',this.listData)
        },
        // 点击图文封面
        modalPicTap (title, index) {
            this.activeIndex = index
            this.modalPic = true;
        },
        // 添加自定义弹窗
        addCustomDialog (editorId) {
            window.UE.registerUI('test-dialog', function (editor, uiName) {
                let dialog = new window.UE.ui.Dialog({
                    iframeUrl: '/admin/widget.images/index.html?fodder=dialog',
                    editor: editor,
                    name: uiName,
                    title: '上传图片',
                    cssRules: 'width:1200px;height:500px;padding:20px;'
                });
                this.dialog = dialog;
                // 参考上面的自定义按钮
                var btn = new window.UE.ui.Button({
                    name: 'dialog-button',
                    title: '上传图片',
                    cssRules: `background-image: url(../../../assets/images/icons.png);background-position: -726px -77px;`,
                    onclick: function () {
                        // 渲染dialog
                        dialog.render();
                        dialog.open();
                    }
                });

                return btn;
            }, 37);
        },
        // 获取图片信息
        getPic (pc) {
            this.$nextTick(() => {
                this.listData[this.activeIndex].pic = pc.att_dir;
                this.modalPic = false;
            })
        },
        // 删除
        bindDelete (item, index) {
            if (this.listData.length == 1) {
                this.lastObj = this.listData[0]
            }
            this.listData.splice(index, 1)
            // this.$emit('parentFun',this.listData)
        }
    }
}
