import { keywordListApi, keywordsetStatusApi, downloadReplyCode } from '@/api/app';
import { mapState } from 'vuex';
export default {
    name: 'keyword',
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            loading: false,
            formValidate: {
                key: '',
                type: '',
                page: 1,
                limit: 20
            },
            tabList: [],
            total: 0,
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '关键字',
                    key: 'key',
                    minWidth: 120
                },
                {
                    title: '回复类型',
                    key: 'typeName',
                    minWidth: 150
                },
                {
                    title: '是否显示',
                    slot: 'status',
                    minWidth: 120
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 120
                }
            ],
            modal: false,
            qrcode: ''
        }
    },
    created () {
        this.getList();
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 75;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    methods: {
        // 下载二维码
        download (item) {
            this.$Spin.show();
            downloadReplyCode(item.id).then(res => {
                this.$Spin.hide();
                this.modal = true;
                this.qrcode = res.data.url;
            }).catch(err => {
                this.$Spin.hide();
                this.$Message.error(err.msg);
            });
        },
        // 列表
        getList () {
            this.loading = true;
            keywordListApi(this.formValidate).then(async res => {
                let data = res.data
                this.tabList = data.list;
                this.total = res.data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index;
            this.getList();
        },
        // 修改是否显示
        onchangeIsShow (row) {
            let data = {
                id: row.id,
                status: row.status
            }
            keywordsetStatusApi(data).then(async res => {
                this.$Message.success(res.msg);
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 表格搜索
        userSearchs () {
            this.formValidate.page = 1;
            this.getList();
        },
        // 添加
        add () {
            // this.$router.push({ path: '/admin/app/wechat/reply/keyword/save/0' });
            this.$emit('keyword-action', '0');
        },
        // 编辑
        edit (row) {
            // this.$router.push({ path: '/admin/app/wechat/reply/keyword/save/' + row.id });
            this.$emit('keyword-action', `${row.id}`);
        },
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `app/wechat/keyword/${row.id}`,
                method: 'DELETE',
                ids: ''
            }
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.tabList.splice(num, 1);
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        }
    }
}
