import { mapState } from "vuex";
import {getGroupChatList} from "@/api/work";
import timeOptions from "@/utils/timeOptions";
export default {
  name: "",
  data() {
    return {
      loading: false,
      formInline:{},
      options: timeOptions,
      columns1: [
        {
          title: "二维码",
          slot: "qr_code",
          minWidth: 80,
          align: 'center'
        },
        {
          title: "二维码名称",
          key: "name",
          minWidth: 100,
          align: 'center'
        },
        {
          title: "群名称",
          key: "group_name",
          minWidth: 100,
          align: 'center'
        },
        {
          title: "群聊",
          slot: "chat_list",
          minWidth: 100,
          align: 'center'
        },
        {
          title: "自动创建",
          slot: "auth_group_chat",
          minWidth: 100,
          align: 'center'
        },
        {
          title: "标签",
          slot: "label_list",
          minWidth: 100,
          align: 'center'
        },
        {
          title: "创建时间",
          key: "create_time",
          minWidth: 130,
          align: 'center'
        },
        {
          title: "操作",
          slot: "action",
          // fixed: "right",
          minWidth: 130,
          align: 'center'
        },
      ],
      tableData: [],
      grid: {
        xl: 7,
        lg: 10,
        md: 12,
        sm: 24,
        xs: 24,
      },
      timeVal:[],
      tableFrom: {
        name:"",
        create_time:"",
        page: 1,
        limit: 15,
      },
      total: 0,
    };
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 80;
    },
    labelPosition() {
      return this.isMobile ? "top" : "left";
    },
  },
  created() {
    this.getList();
  },
  methods: {
    getList(){
      this.loading = true;
      getGroupChatList(this.tableFrom).then(res=>{
        this.tableData = res.data;
        this.loading = false;
      }).catch(err=>{
        this.$Message.error(err.msg);
        this.loading = false;
      })
    },
    search(){
      this.tableFrom.page = 1;
      this.getList();
    },
    onchangeTime(e){
      this.timeVal = e;
      this.tableFrom.create_time = this.timeVal.join("-");
      this.tableFrom.page = 1;
      this.getList();
    },
    downItem(row,index){
      let link = document.createElement('a')
      let url =  row.qr_code;  //codeIMG  要下载的路径
      // 这里是将url转成blob地址，
      fetch(url).then(res => res.blob()).then(blob => { 
        // 将链接地址字符内容转变成blob地址
        link.href = URL.createObjectURL(blob)
        link.download = row.name  
         document.body.appendChild(link)
         link.click()  
     })
    },
    editData(row,index){
      this.$router.push({ path: "/admin/work/addAuthGroup/" + row.id });
    },
    delItem(row,index){
      let delfromData = {
        title: '删除该自动拉群',
        num:index,
        url: `work/group_chat_auth/${row.id}`,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          if(this.tableData.list.length == 1){
            this.tableFrom.page = this.tableFrom.page -1;
            this.getList();
          }else{
            this.getList();
          }
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    pageChange(index){
      this.tableFrom.page = index;
      this.getList();
    }
  },
};
