result:
	.giveGoods{
		.item{
			padding: 0.14rem 0;
			border-top: 1px solid #eee;
			.picTxt{
				.pictrue{
					width: 0.76rem;
					height: 0.76rem;
					border-radius: 0.06rem;
					background-color: #F5F5F5;
					color: #2a7efb;
					.iconfont{
						font-size: 0.34rem;
					}
					img{
						width: 100%;
						height: 100%;
						border-radius: 0.06rem;
					}
					margin-right: 0.16rem;
				}
				.texts{
					width: 3.6rem;
					color: #999999;
					font-size: 0.2rem;
					.name{
						color: #333;
					}
					.limit{
						font-size: 0.2rem;
						margin-top: 0.04rem;
					}
				}
			}
			.num{
				color: #999999;
				font-size: 0.2rem;
			}
		}
	}
    input {
        display: block;
        height: 100%;
        background: none;
        color: inherit;
        opacity: 1;
        -webkit-text-fill-color: currentcolor;
        font: inherit;
        line-height: inherit;
        letter-spacing: inherit;
        text-align: inherit;
        text-indent: inherit;
        text-transform: inherit;
        text-shadow: inherit;
    }

    .pos-order-goods {
        padding: 0 0.3rem;
        background-color: #fff;
    }

    .pos-order-goods .goods {
        height: 1.85rem;
    }

    .pos-order-goods .goods ~ .goods {
        border-top: 1px dashed #e5e5e5;
    }

    .pos-order-goods .goods .picTxt {
        width: 5.15rem;
    }

    .pos-order-goods .goods .picTxt .pictrue {
        width: 1.3rem;
        height: 1.3rem;
    }

    .pos-order-goods .goods .picTxt .pictrue img {
        width: 100%;
        height: 100%;
        border-radius: 0.06rem;
    }

    .pos-order-goods .goods .picTxt .text {
        width: 3.65rem;
        height: 1.3rem;
    }

    .pos-order-goods .goods .picTxt .text .info {
        font-size: 0.28rem;
        color: #282828;
    }

    .pos-order-goods .goods .picTxt .text .attr {
        font-size: 0.2rem;
        color: #999;
        height: 0.8rem;
        line-height: 0.8rem;
				width: 3.5rem;
    }

    .pos-order-goods .goods .money {
        width: 1.64rem;
        text-align: right;
        font-size: 0.28rem;
    }

    .pos-order-goods .goods .money .x-money {
        color: #282828;
    }

    .pos-order-goods .goods .money .num {
        color: #ff9600;
        margin: 0.05rem 0;
    }

    .pos-order-goods .goods .money .y-money {
        color: #999;
        text-decoration: line-through;
    }

    .order-details .header {
        padding: 0 0.3rem;
        height: 1.5rem;
    }

    .order-details .header.on {
        background-color: #666 !important;
    }

    .order-details .header .pictrue {
        width: 1.1rem;
        height: 1.1rem;
    }

    .order-details .header .pictrue img {
        width: 100%;
        height: 100%;
    }

    .order-details .header .data {
        color: rgba(255, 255, 255, 0.8);
        font-size: 0.24rem;
        margin-left: 0.27rem;
    }

    .order-details .header.on .data {
        margin-left: 0;
    }

    .order-details .header .data .state {
        font-size: 0.3rem;
        font-weight: bold;
        color: #fff;
        margin-bottom: 0.07rem;
    }

    .order-details .nav {
        background-color: #fff;
        font-size: 0.26rem;
        color: #282828;
        padding: 0.25rem 0;
    }

    .order-details .nav .navCon {
        padding: 0 0.4rem;
    }

    .order-details .nav .navCon .on {
        font-weight: bold;
        color: #e93323;
    }

    .order-details .nav .progress {
        padding: 0 0.65rem;
        margin-top: 0.1rem;
    }

    .order-details .nav .progress .line {
        width: 1rem;
        height: 0.02rem;
        background-color: #939390;
    }

    .order-details .nav .progress .iconfontYI {
        font-size: 0.25rem;
        color: #939390;
        margin-top: -0.02rem;
        width: 0.3rem;
        height: 0.3rem;
        line-height: 0.33rem;
        text-align: center;
        margin-right: 0 !important;
    }

    .order-details .address {
        font-size: 0.26rem;
        color: #868686;
        background-color: #fff;
        padding: 0.25rem 0.3rem 0.3rem 0.3rem;
    }

    .order-details .address .name {
        font-size: 0.3rem;
        color: #282828;
        margin-bottom: 0.1rem;
    }

    .order-details .address .name .phone {
        margin-left: 0.4rem;
    }

    .order-details .line {
        width: 100%;
        height: 0.03rem;
    }

    .order-details .line img {
        width: 100%;
        height: 100%;
        display: block;
    }

    .order-details .wrapper {
        background-color: #fff;
        margin-top: 0.12rem;
        padding: 0.3rem;
    }

    .order-details .wrapper .item {
        font-size: 0.28rem;
        color: #282828;
    }

    .order-details .wrapper .item ~ .item {
        margin-top: 0.2rem;
    }

    .order-details .wrapper .item .conter {
        color: #868686;
        width: 4.5rem;
		display: flex;
		flex-wrap: nowrap;
		justify-content: flex-end;
		text-align: right;
		.pictrue{
			width: 0.8rem;
			height: 0.8rem;
			margin-left: 0.06rem;
			img{
				width: 100%;
				height: 0.8rem;
				border-radius: 0.06rpx;
			}
		}
    }

    .order-details .wrapper .item .conter .copy {
        font-size: 0.2rem;
        color: #333;
        border-radius: 0.03rem;
        border: 1px solid #666;
        padding: 0.03rem 0.15rem;
        margin-left: 0.24rem;
    }

    .order-details .wrapper .actualPay {
        border-top: 0.01rem solid #eee;
        margin-top: 0.3rem;
        padding-top: 0.3rem;
    }

    .order-details .wrapper .actualPay .money {
        font-weight: bold;
        font-size: 0.3rem;
    }

    .order-details .footer {
        width: 100%;
        height: 1rem;
        position: fixed;
        bottom: 0;
        left: 0;
        background-color: #fff;
        padding: 0 0.3rem;
        border-top: 1px solid #eee;
    }

    .order-details .footer .bnt {
        width: auto;
        height: 0.6rem;
        text-align: center;
        line-height: 0.6rem;
        border-radius: 0.5rem;
        color: #fff;
        font-size: 0.27rem;
        padding: 0 3%;
    }

    .order-details .footer .bnt.cancel {
        color: #aaa;
        border: 1px solid #ddd;
    }

	.order-details .footer .bnt.on{
		color: #c5c8ce!important;
		background: #f7f7f7!important;
		border: 1px solid #dcdee2!important;
	}

    .order-details .footer .bnt.default {
        color: #444;
        border: 1px solid #444;
    }

    .order-details .footer .bnt ~ .bnt {
        margin-left: 0.18rem;
    }

    .pos-order-details .header {
        background: linear-gradient(to right, #2291f8 0%, #1cd1dc 100%);
        background: -webkit-linear-gradient(to right, #2291f8 0%, #1cd1dc 100%);
        background: -moz-linear-gradient(to right, #2291f8 0%, #1cd1dc 100%);
    }

    .pos-order-details .header .state {
        font-size: 0.3rem;
        color: #fff;
    }

    .pos-order-details .header .data {
        font-size: 0.2rem;
    }

    .pos-order-details .header .data .order-num {
        font-size: 0.27rem;
        margin-bottom: 0.08rem;
    }

    .pos-order-details .remarks {
        width: 100%;
        height: 0.86rem;
        background-color: #fff;
        padding: 0 0.3rem;
        line-height: 0.86rem
    }

    .pos-order-details .remarks .iconfontYI {
        font-size: 0.4rem;
        color: #2a7efb;
    }

    .pos-order-details .remarks input {
        width: 6.3rem;
        height: 100%;
        font-size: 0.3rem;
    }

    .pos-order-details .remarks input::placeholder {
        color: #666;
    }

    .pos-order-details .orderingUser {
        font-size: 0.26rem;
        color: #282828;
        padding: 0 0.3rem;
        height: 0.67rem;
        background-color: #fff;
        margin-top: 0.16rem;
        border-bottom: 1px solid #f5f5f5;
    }

    .pos-order-details .orderingUser .iconfontYI {
        font-size: 0.4rem;
        color: #2a7efb;
        margin-right: 0.15rem;
    }

    .pos-order-details .address {
        margin-top: 0;
    }

    .pos-order-details .pos-order-goods {
        margin-top: 0.17rem;
    }

    .pos-order-details .footer .more {
        font-size: 0.27rem;
        color: #aaa;
        width: 1rem;
        height: 0.64rem;
        text-align: center;
        line-height: 0.64rem;
        margin-right: 0.25rem;
        position: relative;
    }

    .pos-order-details .footer .delivery {
        background: linear-gradient(to right, #2291f8 0%, #1cd1dc 100%);
        background: -webkit-linear-gradient(to right, #2291f8 0%, #1cd1dc 100%);
        background: -moz-linear-gradient(to right, #2291f8 0%, #1cd1dc 100%);
    }

    .pos-order-details .footer .more .order .arrow {
        width: 0;
        height: 0;
        border-left: 0.11rem solid transparent;
        border-right: 0.11rem solid transparent;
        border-top: 0.2rem solid #e5e5e5;
        position: absolute;
        left: 0.15rem;
        bottom: -0.18rem;
    }

    .pos-order-details .footer .more .order .arrow:before {
        content: '';
        width: 0;
        height: 0;
        border-left: 0.09rem solid transparent;
        border-right: 0.09rem solid transparent;
        border-top: 0.19rem solid #fff;
        position: absolute;
        left: -0.1rem;
        bottom: 0;
    }

    .pos-order-details .footer .more .order {
        width: 2rem;
        background-color: #fff;
        border: 1px solid #eee;
        border-radius: 0.1rem;
        position: absolute;
        top: -2rem;
        z-index: 9;
    }

    .pos-order-details .footer .more .order .item {
        height: 0.77rem;
        line-height: 0.77rem;
    }

    .pos-order-details .footer .more .order .item ~ .item {
        border-top: 1px solid #f5f5f5;
    }

    .pos-order-details .footer .more .moreName {
        width: 100%;
        height: 100%;
    }

    .public-total {
        font-size: 0.28rem;
        color: #282828;
        border-top: 1px solid #eee;
        height: 0.92rem;
        line-height: 0.92rem;
        text-align: right;
        padding: 0 0.3rem;
        background-color: #fff;
    }

    .public-total .money {
        color: #ff4c3c;
    }
