import { mapState } from 'vuex';
import editFrom from '@/components/from/from';
import { classListApi, classAddApi, classEditApi, setStatusApi } from '@/api/system';
export default {
    name: 'configTab',
    components: { editFrom },
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            loading: false,
            formValidate: {
                status: '',
                page: 1,
                limit: 100,
                title: ''
            },
            total: 0,
            FromData: null,
            classId: 0,
            classList: []
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 75;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    mounted () {
        this.getList();
    },
    methods: {
        // 跳转到配置列表页面
        goList (row) {
            this.$router.push({ path: '/admin/system/config/system_config_tab/list/' + row.id });
        },
        // 添加配置分类
        classAdd () {
            classAddApi().then(async res => {
                this.FromData = res.data;
                this.$refs.edits.modals = true;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 编辑
        edit (row) {
            classEditApi(row.id).then(async res => {
                if(res.data.status === false){
                    return this.$authLapse(res.data);
                }
                this.FromData = res.data;
                this.$refs.edits.modals = true;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 删除
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `setting/config_class/${row.id}`,
                method: 'DELETE',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                // this.classList.splice(num, 1);
                this.getList();
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 列表
        getList () {
            this.loading = true;
            this.formValidate.status = this.formValidate.status || '';
            classListApi(this.formValidate).then(async res => {
                let data = res.data
                this.classList = data.list;
                this.total = data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index;
            this.getList();
        },
        // 表格搜索
        userSearchs () {
            this.formValidate.page = 1;
            this.getList();
        },
        // 修改成功
        submitFail () {
            //this.getList();
        },
        // 修改是否显示
        onchangeIsShow (row) {
            let data = {
                id: row.id,
                status: row.status
            }
            setStatusApi(data).then(async res => {
                this.$Message.success(res.msg);
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        }
    }
}
