.words-mask {
    z-index: 50;
    position: fixed;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);

    .content {
        position: absolute;
        left: 0;
        right: 0;
        top: 1.14rem;
        bottom: 0;
        display: flex;
        flex-direction: column;
        background: #fff;
        border-radius: 0.06rem 0.06rem 0px 0px;

        .title-box {
            padding: 0 .3rem .3rem;
            position: relative;
            border-bottom 1px solid #F5F6F9
            .tab-box{
                position relative
                display flex
                justify-content space-between
                padding .4rem 2.2rem .3rem
                font-size .32rem
                color #9F9F9F
                .on{
                    color #3875EA
                    font-weight bold
                }
                .right-icon{
                    position absolute
                    right 0
                    top 50%
                    transform translateY(-50%)
                    .iconfont{
                        margin-left .2rem
                        font-size .48rem
                        color #C8CAD0
                    }
                }
            }
            .input-box{
                display: flex;
                align-items: center;
                width: 6.9rem;
                height: .64rem;
                padding-right: 0.05rem;
                margin-left: .18rem;
                border-radius: .32rem;
                overflow hidden

                >>> .ivu-input{
                    background #F5F6F9
                }
                >>> .ivu-input, .ivu-input:hover, .ivu-input:focus {
                    border transparent
                    box-shadow: none;
                }
            }
            .icon-cha1 {
                position: absolute;
                right: 0;
                top: 50%;
                transform: translateY(-50%);
            }
        }

        .scroll-box {
            flex: 1;
            display flex
            overflow: hidden;
            .scroll-left{
                width 1.76rem
                height 100%
                overflow-y scroll
                -webkit-overflow-scrolling touch
                background #F5F6F9
                .left-item{
                    position relative
                    display flex
                    align-items center
                    justify-content center
                    width 100%
                    height 1.09rem
                    color #282828
                    font-size .26rem
                    &.active{
                        color #3875EA
                        background #fff
                        &:after{
                            content ' '
                            position: absolute;
                            left 0
                            top 50%
                            transform translateY(-50%)
                            width 0.06rem
                            height .46rem
                            background #3875EA
                        }
                    }
                    &.add_cate{
                        color #9F9F9F
                        font-size .26rem
                        .iconfont{
                            margin-right 0.1rem
                            font-size .24rem
                        }
                    }
                }
            }
            .right-box{
                flex 1
                overflow scroll
                -webkit-overflow-scrolling touch
            }
            .msg-item {
                padding: .25rem .3rem;
                color #888888
                font-size .28rem
                .title{
                    margin-right .2rem
                    color #282828
                }
                &.add-mg{
                    display flex
                    align-items center
                    justify-content flex-end
                    font-size .28rem
                    padding .15rem .3rem
                    .iconfont{
                        font-size .36rem
                        margin-right .1rem
                    }
                }
            }
        }
    }
}

.words-box{
    .mask-title{
        position relative
        text-align center
        margin-bottom .5rem
        color #282828
        font-size .32rem
        font-weight bold
        .iconfont{
            position absolute
            right 0
            top 50%
            transform translateY(-50%)
            color #C8CAD0
            font-size .44rem
            font-weight normal
        }
    }
    .input-box{
        height .68rem
        margin-top .32rem
        background #F5F5F5
        border-radius: .14rem;
        &.text-area{
            height 1.92rem
            textarea{
                display block
                height 100%
            }
        }
    }
    .subBtn{
        width 100%
        height .86rem
        margin-top .6rem
        margin-bottom .3rem
        font-size .3rem !important
        border-radius: .43rem;
        &[disabled]{
            background #C8CAD0
            color #fff
            font-size .3rem !important
        }
    }
}
.edit-box{
    z-index 99
    position fixed
    left 0
    right 0
    top 0
    bottom 0
    display flex
    flex-direction column
    background #fff
    .head{
        padding .4rem .3rem .3rem
        .tit-bar{
            position relative
            text-align center
            font-size .32rem
            color #282828
            font-weight bold
            span{
                position absolute
                right 0
                top 50%
                transform translateY(-50%)
                color #3875EA
                font-size .28rem
                font-weight normal
            }
        }
        .input-box{
            margin-top .3rem
            background #F5F6F9
            border-radius: .39rem;
        }
    }
    .scroll-box {
        flex: 1;
        display flex
        overflow: hidden;
        .scroll-left{
            display flex
            flex-direction column
            position relative
            width 1.76rem
            height 100%
            background #F5F6F9
            .top{
                flex 1
                overflow-y scroll
                -webkit-overflow-scrolling touch
            }

            .left-item{
                position relative
                display flex
                align-items center
                justify-content center
                width 100%
                height 1.09rem
                color #282828
                font-size .26rem
                &.active{
                    color #3875EA
                    background #fff
                    &:after{
                        content ' '
                        position: absolute;
                        left 0
                        top 50%
                        transform translateY(-50%)
                        width 0.06rem
                        height .46rem
                        background #3875EA
                    }
                }
                &.add_cate{
                    color #9F9F9F
                    font-size .26rem
                    .iconfont{
                        margin-right 0.1rem
                        font-size .24rem
                    }
                }
                &.edits-box{
                    color #3875ea
                }
            }
        }
        .right-box{
            flex 1
            padding-left .3rem
        }
        .msg-item {
            padding: .25rem .3rem .25rem 0;
            color #888888
            font-size .28rem
            .title{
                margin-right .2rem
                color #282828
            }
            &.add-mg{
                display flex
                align-items center
                justify-content flex-end
                font-size .28rem
                padding .15rem .3rem
                .iconfont{
                    font-size .36rem
                    margin-right .1rem
                }
            }
            .edit-bar{
                display flex
                align-items center
                justify-content flex-end
                margin-top .25rem
                padding-bottom .1rem
                border-bottom 1px solid #F0F2F7
                .iconfont{
                    margin-left .3rem
                    font-size .32rem
                }
            }
        }
    }
}
.cate-list{
    .list-box{
        max-height 7.5rem
        overflow-y scroll
        -webkit-overflow-scrolling touch
        .item{
            display flex
            align-items center
            justify-content space-between
            height 1rem
            border-bottom 1px solid #F0F2F7
            color #282828
            font-size .28rem
            .iconfont{
                color #9F9F9F
                font-size .32rem
                margin-left .4rem
            }
        }
    }
}
