import { mapState } from "vuex";
import { getWorkMomenttList } from "@api/work";
import timeOptions from "@/utils/timeOptions";
export default {
  name: "",
  data() {
    return {
      loading: false,
      timeVal: [],
      options: timeOptions,
      columns1: [
        {
          title: "任务名称",
          key: "name",
          minWidth: 80,
        },
		{
          title: "已发送成员",
          key: "user_count",
          minWidth: 100,
        },
        {
          title: "未发送成员",
          key: "unuser_count",
          minWidth: 100,
        },
        {
          title: "成员类型",
          slot: "type",
          minWidth: 120,
        },
		{
          title: "发布方式",
          slot: "send_type",
          minWidth: 120,
        },
        {
          title: "发送人",
          slot: "user_list",
          minWidth: 150,
        },
        {
          title: "创建时间",
          key: "create_time",
          minWidth: 150,
        },
        {
          title: "操作",
          slot: "action",
          minWidth: 100,
        },
      ],
      tableData: [],
      tableFrom: {
        time: "",
        name: "",
        page: 1,
        limit: 15,
      },
    };
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 80;
    },
    labelPosition() {
      return this.isMobile ? "top" : "left";
    },
  },
  created() {
    this.getList();
  },
  methods: {
    getList() {
      this.loading = true;
      getWorkMomenttList(this.tableFrom)
        .then((res) => {
          this.tableData = res.data;
          this.loading = false;
        })
        .catch((err) => {
          this.$Message.error(err.msg);
          this.loading = false;
        });
    },
    search() {
      this.tableFrom.page = 1;
      this.getList();
    },
    // 具体日期
    onchangeTime(e) {
      this.timeVal = e;
      this.tableFrom.time = this.timeVal.join("-");
    },
    pageChange(index) {
      this.tableFrom.page = index;
      this.getList();
    },
    detailsItem(row,index){
      this.$router.push("/admin/work/client/moment_info/" + row.id)
    },
    delItem(row,index){
      let delfromData = {
					title: '删除该朋友圈',
					num:index,
					url: `work/moment/${row.id}`,
					method: "DELETE",
					ids: "",
				};
				this.$modalSure(delfromData)
					.then((res) => {
					this.$Message.success(res.msg);
					if(this.tableData.list.length == 1){
						this.tableFrom.page = this.tableFrom.page -1;
						this.getList();
					}else{
						this.getList();
					}
					})
					.catch((res) => {
					this.$Message.error(res.msg);
				});
    },
  },
};
