.empty-box{
  height 170px;
}
.mobile-page {
  position: relative;
  width: auto;
  /*height: 140px;*/

  .banner {
    /*position: absolute;*/
    /*left: 0;*/
    /*top: 0;*/
    width: 100%;
    margin-top 0;
		&.on{
			margin-top -48px;
		}

    img {
      width: 100%;
      height: 100%;
      border-radius: 6px;

      &.doc {
        border-radius: 0;
      }
    }
  }

  .bg {
    width: 100%;
    height: 50px;
    background: linear-gradient(90deg, #F62C2C 0%, #F96E29 100%);
  }
}

.dot {
  position: absolute;
  left: 0;
  bottom: 20px;
  width: 100%;
  display: flex;
  align-items: center;

  &.number{
    bottom: 4px;
  }

  .num{
    width 25px;
    height 18px;
    line-height 18px;
    background-color #000;
    color #fff;
    opacity 0.3;
    border-radius 8px;
    font-size 12px;
    text-align center;
  }

  .dot-item {
    width: 5px;
    height: 5px;
    background: #AAAAAA;
    border-radius: 50%;
    margin: 0 3px;
  }

  &.line-dot {
    bottom: 20px;

    .line_dot-item {
      width: 8px;
      height: 2px;
      background: #AAAAAA;
      margin: 0 3px;
    }
  }
}
