import { mapState } from "vuex";
import {
  storeExtractInfo,
  storeExtractMarkApi,
  staffListInfo,
  storeExtractVerifyApi,
  storepaying,
} from "@/api/store";
import cardsData from "../../../components/cards/cards.vue";
import timeOptions from "@/utils/timeOptions";
export default {
  name: "bill",
  components: {
    cardsData,
  },
  data() {
    return {
      id: 0,
      modal: false,
      modalmark: false,
      formItem: {
        type: "1",
        message: "",
      },
      remarkId: 0,
      remarks: {
        mark: "",
      },
      total: 0,
      cardLists: [],
      extractStatistics: [],
      staff: [],
      loading: false,
      columns: [
        {
          title: "ID",
          key: "id",
          width: 60,
        },
        {
          title: "图片",
          slot: "admin_id",
          width: 60,
        },
        {
          title: "门店",
          slot: "store",
          minWidth: 120,
        },
        {
          title: "申请金额",
          key: "extract_price",
          minWidth: 80,
        },
        {
          title: "申请时间",
          key: "add_time",
          minWidth: 150,
        },
        {
          title: "收款方式",
          slot: "extract_type",
          minWidth: 80,
        },
        {
          title: "收款信息",
          slot: "type",
          minWidth: 200,
        },
        {
          title: "审核状态",
          slot: "status",
          minWidth: 150,
        },
        {
          title: "转账状态",
          slot: "pay_status",
          minWidth: 180,
        },
        {
          title: "管理员",
          key: "admin_name",
          minWidth: 80,
        },
        {
          title: "备注",
          key: "store_mark",
          minWidth: 150,
        },
        {
          title: "操作",
          slot: "action",
          fixed: "right",
          minWidth: 120,
          align: "center",
        },
      ],
      orderList: [],
      formValidate: {
        pay_status: "",
        extract_type: "",
        data: "",
        status: "",
        store_id: "",
        page: 1,
        limit: 15,
      },
      timeVal: [],
      options: timeOptions,
      fromLists: {
        custom: true,
        status: [
          { text: "全部", val: "" },
          { text: "待审核", val: "0" },
          { text: "已通过", val: "1" },
          { text: "未通过", val: "-1" },
        ],
      },
    };
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
  },
  mounted() {
    this.staffApi();
    this.getList();
  },
  methods: {
    getList() {
      this.loading = true;
      storeExtractInfo(this.formValidate).then((res) => {
        this.orderList = res.data.list.list;
        this.total = res.data.list.count;
        this.extractStatistics = res.data.extract_statistics;
        this.cardLists = [
          {
            col: 6,
            count: this.extractStatistics.unPayPrice,
            name: "待转账金额",
            className: "md-basket",
          },
          {
            col: 6,
            count: this.extractStatistics.price,
            name: "待审核金额",
            className: "md-cash",
          },
          {
            col: 6,
            count: this.extractStatistics.price_not,
            name: "可提现金额",
            className: "ios-cash",
          },
          {
            col: 6,
            count: this.extractStatistics.paidPrice,
            name: "累计提现金额",
            className: "ios-cash",
          },
        ];
        this.loading = false;
      });
    },
    staffApi() {
      staffListInfo().then((res) => {
        this.staff = res.data;
      });
    },
    searchs() {
      this.formValidate.page = 1;
      this.getList();
    },
    // 选择时间
    selectChange(tab) {
      this.formValidate.page = 1;
      this.formValidate.data = tab;
      this.timeVal = [];
      this.getList();
    },
    //审核状态
    payStatus(tab) {
      this.formValidate.page = 1;
      this.formValidate.status = tab;
      this.getList();
    },
    // 具体日期
    onchangeTime(e) {
      this.timeVal = e;
      this.formValidate.data = this.timeVal[0] ? this.timeVal.join("-") : "";
      this.formValidate.page = 1;
      this.getList();
    },
    //分页
    pageChange(status) {
      this.formValidate.page = status;
      this.getList();
    },
    remark(data) {
      this.remarkId = data.id;
      this.remarks.mark = data.store_mark;
      this.modalmark = true;
    },
    //审核
    examine(row) {
      this.modal = true;
      this.id = row.id;
    },
    //转账
    paying(row) {
      this.$modalForm(storepaying(row.id)).then(() => this.getList());
    },
    //审核提交
    ok() {
      if (this.formItem.type == 2) {
        if (this.formItem.message == "") {
          this.$Message.error("请填写拒绝原因");
          return;
        }
      }
      storeExtractVerifyApi(this.id, this.formItem)
        .then((res) => {
          this.$Message.success(res.msg);
          this.formItem = {
            type: "1",
            message: "",
          };
          this.modal = false;
          this.getList();
        })
        .catch((err) => {
          this.$Message.error(err.msg);
          this.modal = false;
        });
    },
    // 取消按钮
    cancel() {
      this.formItem = {
        type: "1",
        message: "",
      };
      this.remarks.mark = "";
      this.modal = false;
      this.modalmark = false;
    },
    //备注的提交
    putRemark() {
      storeExtractMarkApi(this.remarkId, this.remarks)
        .then((res) => {
          this.$Message.success(res.msg);
          this.modalmark = false;
          this.getList();
        })
        .catch((err) => {
          this.$Message.error(err.msg);
        });
    },
  },
};
