import { mapState } from 'vuex';
import { userGroupApi, groupAddApi } from '@/api/user';
export default {
    name: "user_group",
    data() {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            loading: false,
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '分组名称',
                    key: 'group_name',
                    minWidth: 600
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 120,
                    maxWidth: 140
                }
            ],
            groupFrom: {
                page: 1,
                limit: 10
            },
            groupLists: [],
            total:0
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 75;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'left';
        }
    },
    created () {
        this.getList();
    },
    methods:{
        // 添加
        add () {
            this.$modalForm(groupAddApi(0)).then(() => this.getList());
        },
        // 分组列表
        getList () {
            this.loading = true;
            userGroupApi(this.groupFrom).then(async res => {
                let data = res.data;
                this.groupLists = data.list;
                this.total = data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.groupFrom.page = index;
            this.getList();
        },
        //修改
        edit(id){
            this.$modalForm(groupAddApi(id)).then(() => this.getList());
        },
        // 删除
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `user/user_group/del/${row.id}`,
                method: 'DELETE',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.groupLists.splice(num, 1);

                this.getList();
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
    }
}
