import cardsData from "@/components/cards/cards";
import searchFrom from "@/components/publicSearchFrom";
import { mapState } from "vuex";
import { cashListApi, cashEditApi, refuseApi } from "@/api/finance";
import { formatDate } from "@/utils/validate";
import editFrom from "@/components/from/from";
import timeOptions from "@/utils/timeOptions";
export default {
  name: "cashApply",
  components: { cardsData, searchFrom, editFrom },
  filters: {
    formatDate(time) {
      if (time !== 0) {
        let date = new Date(time * 1000);
        return formatDate(date, "yyyy-MM-dd hh:mm");
      }
    },
  },
  data() {
    return {
      modal_loading: false,
      options: timeOptions,
      fail_msg: {
        message: "输入信息不完整或有误!",
      },
      modals: false,
      total: 0,
      cardLists: [],
      loading: false,
      columns: [
        {
          title: "ID",
          key: "id",
          width: 80,
        },
        {
          title: "用户信息",
          slot: "nickname",
          minWidth: 180,
        },
        {
          title: "提现到账金额",
          slot: "extract_price",
          minWidth: 90,
        },
        {
          title: "手续费",
          slot: "extract_fee",
          minWidth: 90,
        },
        {
          title: "提现方式",
          slot: "extract_type",
          minWidth: 150,
        },
        {
          title: "收款码",
          slot: "qrcode_url",
          minWidth: 150,
        },
        {
          title: "添加时间",
          slot: "add_time",
          minWidth: 100,
        },
        {
          title: "备注",
          key: "mark",
          minWidth: 100,
        },
        {
          title: "审核状态",
          slot: "status",
          minWidth: 180,
        },
        {
          title: "操作",
          slot: "createModalFrame",
          fixed: "right",
          width: 100,
        },
      ],
      tabList: [],
      fromList: {
        title: "选择时间",
        custom: true,
        fromTxt: [
          { text: "全部", val: "" },
          { text: "昨天", val: "yesterday" },
          { text: "今天", val: "today" },
          { text: "本周", val: "week" },
          { text: "本月", val: "month" },
          { text: "本季度", val: "quarter" },
          { text: "本年", val: "year" },
        ],
      },
      treeData: {
        withdrawal: [
          {
            title: "全部",
            value: "",
          },
          {
            title: "未通过",
            value: -1,
          },
          {
            title: "申请中",
            value: 0,
          },
          {
            title: "已通过",
            value: 1,
          },
        ],
        payment: [
          {
            title: "全部",
            value: "",
          },
          {
            title: "支付宝",
            value: "alipay",
          },
          {
            title: "银行卡",
            value: "bank",
          },
          {
            title: "微信",
            value: "wx",
          },
          // {
          // 	title: '提现到余额',
          // 	value: 'balance'
          // }
        ],
      },
      formValidate: {
        status: "",
        extract_type: "",
        nireid: "",
        data: "",
        page: 1,
        limit: 20,
      },
      extractStatistics: {},
      timeVal: [],
      FromData: null,
      extractId: 0,
    };
  },
  watch: {
    $route() {
      if (
        this.$route.fullPath === "/admin/finance/user_extract/index?status=0"
      ) {
        this.getPath();
      }
    },
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
  },
  // created() {
  //     if(this.$route.query.key == 0){
  //         this.formValidate.status = parseInt(this.$route.query.key)
  //     }
  // },
  mounted() {
    if (this.$route.fullPath === "/admin/finance/user_extract/index?status=0") {
      this.getPath();
    } else {
      this.getList();
    }
    // this.getList();
  },
  methods: {
    getPath() {
      this.formValidate.page = 1;
      this.formValidate.status = parseInt(this.$route.query.status);
      this.getList();
    },
    // 无效
    invalid(row) {
      this.extractId = row.id;
      this.modals = true;
    },
    // 确定
    oks() {
      this.modal_loading = true;
      refuseApi(this.extractId, this.fail_msg)
        .then(async (res) => {
          this.$Message.success(res.msg);
          this.modal_loading = false;
          this.modals = false;
          this.getList();
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 通过
    adopt(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `finance/extract/adopt/${row.id}`,
        method: "put",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          this.getList();
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 具体日期
    onchangeTime(e) {
      this.timeVal = e;
      this.formValidate.data = this.timeVal[0] ? this.timeVal.join("-") : "";
      this.formValidate.page = 1;
      this.getList();
    },
    // 选择时间
    selectChange(tab) {
      this.formValidate.page = 1;
      this.formValidate.data = tab;
      this.timeVal = [];
      this.getList();
    },
    // 选择
    selChange() {
      this.formValidate.page = 1;
      this.getList();
    },
    reset(){
      this.selChange = "";
      this.formValidate = [];
      this.timeVal = [];
      // this.$refs.formValidate.resetFields()
      this.getList();
    },
    // 列表
    getList() {
      this.loading = true;
      cashListApi(this.formValidate)
        .then(async (res) => {
          let data = res.data;
          this.tabList = data.list.list;
          this.total = data.list.count;
          this.extractStatistics = data.extract_statistics;
          this.cardLists = [
            {
              col: 6,
              count: this.extractStatistics.price,
              name: "待提现金额",
              className: "md-basket",
            },
            {
              col: 6,
              count: this.extractStatistics.brokerage_count,
              name: "佣金总金额",
              className: "md-pricetags",
            },
            {
              col: 6,
              count: this.extractStatistics.priced,
              name: "已提现金额",
              className: "md-cash",
            },
            {
              col: 6,
              count: this.extractStatistics.brokerage_not,
              name: "未提现金额",
              className: "ios-cash",
            },
          ];
          this.loading = false;
        })
        .catch((res) => {
          this.loading = false;
          this.$Message.error(res.msg);
        });
    },
    pageChange(index) {
      this.formValidate.page = index;
      this.getList();
    },
    // 编辑
    edit(row) {
      cashEditApi(row.id)
        .then(async (res) => {
          if (res.data.status === false) {
            return this.$authLapse(res.data);
          }
          this.FromData = res.data;
          this.$refs.edits.modals = true;
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 编辑提交成功
    submitFail() {
      this.getList();
    },
  },
};
