import { mapState } from "vuex"; 
import {getWelcomeList} from "@/api/work"
export default {
  name: "",
  data() {
    return {
      loading: false,
      formInline:{},
      columns1: [
        {
          title: "欢迎语内容",
          key: "content",
          minWidth: 80,
        },
        // {
        //   title: "使用成员",
        //   key: "userids",
        //   minWidth: 100,
        //   align: 'center'
        // },
        {
          title: "类型",
          slot: "type",
          minWidth: 80,
        },
        {
          title: "排序",
          key: "sort",
          minWidth: 80,
        },
        {
          title: "创建时间",
          key: "create_time",
          minWidth: 130,
        },
        {
          title: "操作",
          slot: "action",
          // fixed: "right",
          minWidth: 130,
        },
      ],
      tableData: [],
      grid: {
        xl: 7,
        lg: 10,
        md: 12,
        sm: 24,
        xs: 24,
      },
      tableFrom: {
        // start_status: "",
        // status: "",
        // store_name: "",
        page: 1,
        limit: 15,
      },
      total: 0,
    };
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 80;
    },
    labelPosition() {
      return this.isMobile ? "top" : "left";
    },
  },
  created() {
    this.getList();
  },
  methods: {
    //编辑
    editData(row,index){
      this.$router.push({ path: "/admin/work/addWelcome/" + row.id });
    },
    //删除
    delData(row,index){
      let delfromData = {
        title: '删除欢迎语',
        num:index,
        url: `/work/welcome/${row.id}`,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          if(this.tableData.list.length == 1){
            this.tableFrom.page = this.tableFrom.page -1;
            this.getList();
          }else{
            this.getList();
          }
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    getList(){
      this.loading = true;
      getWelcomeList(this.tableFrom).then(res=>{
        this.tableData = res.data;
        this.loading = false;
      }).catch(err=>{
        this.$Message.error(err.msg);
        this.loading = false;
      })
    },
    //分页
    pageChange(index){
      this.tableFrom.page = index;
      this.getList()
    }
  },
};
