import cardsData from "@/components/cards/cards";
import { workGroupTemplateInfo,groupTemplateMemberList,groupTemplateClientList,workGroupTemplateSendMsg } from "@/api/work";
 import { formatDate } from '@/utils/validate';
export default {
  data() {
    return {
      cardLists: [],
      optionData: {},
      style: { height: "400px" },
      spinShow: false,
      tableColumn: [
        {
          title: "成员",
          key: "name",
          minWidth: 100,
        },
        {
          title: "发送状态",
          slot: "status",
          minWidth: 100,
        },
        {
          title: "发送数目",
          slot: "sendResult",
          minWidth: 100,
        },
        {
          title: "发送时间",
          slot: "send_time",
          minWidth: 100,
        },
        {
          title: "操作",
          slot: "action",
          minWidth: 100,
        },
      ],
      tableColumn1:[
        {
          title: "客户",
          key: "name",
          minWidth: 100,
        },
        {
          title: "发送状态",
          slot: "status1",
          minWidth: 100,
        },
        {
          title: "发送时间",
          slot: "send_time",
          minWidth: 100,
        },
      ],
      tabIndex:0,
      tableData: [],
      userLoading: false,
      timeVal: [],
      tableForm: {
        page: 1,
        limit: 15,
        client_name:"", //客户名称
        user_name:"", //成员名称
        status:"",
        status1:"",
        id: 0,
      },

    };
  },
  filters: {
      formatDate (time) {
          if (time !== 0) {
              let date = new Date(time * 1000);
              return formatDate(date, 'yyyy-MM-dd hh:mm');
          }
      }
  },
  components: { cardsData },
  mounted() {
    this.tableForm.id = this.$route.params.id;
    this.getData();
    this.getMemberList();
    // this.getList();
  },
  methods: {
    getList() {
      this.userLoading = true;
      workGroupStatisticsList(this.tableForm)
        .then((res) => {
          this.tableData = res.data;
          this.userLoading = false;
        })
        .catch((err) => {
          this.userLoading = false;
          this.$Message.error(err.msg);
        });
    },
    getData() {
      workGroupTemplateInfo(this.$route.params.id).then((res) => {
        this.cardLists = [
          {
            col: 6,
            count: res.data.user_count,
            name: "已发送成员",
            type:1,
            className: "iconjinrixinzeng",
          },
          {
            col: 6,
            count: res.data.external_user_count,
            name: "送达客户",
            type:1,
            className: "iconjinrituiqun",
          },
          {
            col: 6,
            count: res.data.unuser_count,
            name: "未发送成员",
            type:1,
            className: "icondangqianqunchengyuan",
          },
          {
            col: 6,
            count: res.data.external_unuser_count,
            name: "未送达客户",
            type:1,
            className: "iconleijituiqun",
          },
        ];

      });
    },
    getMemberList(){
      groupTemplateMemberList(this.tableForm.id,{
        user_name:this.tableForm.user_name,
        status:this.tableForm.status,
        page:this.tableForm.page,
        limit:this.tableForm.limit
      }).then(res=>{
        this.tableData = res.data;
      })
    },
    getClientList(){
      groupTemplateClientList(this.tableForm.id,{
        client_name:this.tableForm.user_name,
        status:this.tableForm.status1,
        page:this.tableForm.page,
        limit:this.tableForm.limit
      }).then(res=>{
        this.tableData = res.data;
      })
    },
    sendMsg(row,index){
      workGroupTemplateSendMsg({
					userid:row.userid,
					time:row.create_time,
					id:""
				}).then(res=>{
					this.$Message.success(res.msg)
				}).catch(err=>{
					this.$Message.error(err.msg);
				})
    },
    onChangeType(){
      if(this.tabIndex == 0){
        this.getMemberList();
      }else{
        this.getClientList();
      }
    },
    search(){
      if(this.tabIndex == 0){
        this.getMemberList();
      }else{
        this.getClientList();
      }

    },
    pageChange(index) {
      this.tableForm.page = index;
      if(this.tabIndex == 0){
        this.getMemberList();
      }else{
        this.getClientList();
      }
    },
  },
};
