.pictrueBox {
    display: inline-block;
    .upLoad {
        width: 58px;
        height: 58px;
        line-height: 58px;
        border: 1px dotted rgba(0, 0, 0, 0.1);
        border-radius: 4px;
        background: rgba(0, 0, 0, 0.02);
        cursor: pointer;
        .input-display {
            display: none
        }
    }
    .pictrue {
        width: 60px;
        height: 60px;
        border: 1px dotted rgba(0, 0, 0, 0.1);
        margin-right: 15px;
        margin-bottom: 10px;
        display: inline-block;
        position: relative;
        cursor: pointer;

        img {
            width: 100%;
            height: 100%;
        }

        .btndel {
            position: absolute;
            z-index: 1;
            width: 20px !important;
            height: 20px !important;
            left: 46px;
            top: -4px;
        }
    }
}
.tips{
    font-size: 12px;
    font-weight: 400;
    color: #ccc;
}
.imgPic{
    .info{
        width: 60%;
        margin-left: 10px;
    }
    .pictrue{
        height: 36px;
        margin: 7px 3px 0 3px;
        img{
            height: 100%;
            display: block;
        }
    }
}
.iview-video-style {
    width: 40%;
    height: 180px;
    border-radius: 10px;
    background-color: #707070;
    margin-top: 10px;
    position: relative;
    overflow: hidden;
    .video-style {
        width: 100%;
        height: 100% !important;
        border-radius: 10px;
    }
    .iconv{
        color: #fff;
        line-height: 180px;
        width: 50px;
        height: 50px;
        display: inherit;
        font-size: 26px;
        position: absolute;
        top: -74px;
        left: 50%;
        margin-left: -25px;
    }
    .mark{
        position: absolute;
        width: 100%;
        height: 30px;
        top: 0;
        background-color: rgba(0, 0, 0, 0.5);
        text-align: center;
    }
}
.fixed-card {
    position: fixed;
    right: 0;
    bottom: 0;
    left: 200px;
    z-index: 20;
    box-shadow: 0 -1px 2px rgb(240, 240, 240);

    /deep/ .ivu-card-body {
        padding: 15px 16px 14px;
    }

    .ivu-form-item {
        margin-bottom: 0!important;
    }

    /deep/ .ivu-form-item-content {
        margin-right: 124px;
        text-align: center;
    }

    .ivu-btn {
        height: 36px;
        padding: 0 20px;
    }
}
