import vuedraggable from 'vuedraggable'
import uploadPictures from '@/components/uploadPictures';
import linkaddress from '@/components/linkaddress';
export default {
    name: "c_foot",
    props:{
        configObj:{
            type:Object,
            default:function () {
                return {}
            }
        },
        configNme:{
            type:String,
            default:''
        },
    },
    components: {
        uploadPictures,
        linkaddress,
        draggable: vuedraggable,
    },
    data(){
        return{
            val1:'',
            val2:'',
            footConfig:[],
            modalPic:false,
            isChoice: '单选',
            itemIndex:0,
            itemChildIndex:0,
            gridBtn: {
                xl: 4,
                lg: 8,
                md: 8,
                sm: 8,
                xs: 8
            },
            gridPic: {
                xl: 6,
                lg: 8,
                md: 12,
                sm: 12,
                xs: 12
            },
        }
    },
    watch: {
        configObj: {
            handler (nVal, oVal) {
                this.footConfig = nVal[this.configNme]
            },
            deep: true
        }
    },
    created() {
        this.footConfig = this.configObj[this.configNme]
    },
    methods:{
        linkUrl(e){
            this.footConfig[this.itemIndex].link = e
        },
        getLink (index){
            this.itemIndex = index
            this.$refs.linkaddres.modals = true
        },
        // 点击图文封面
        modalPicTap (parent,child) {
            this.itemIndex = parent;
            this.itemChildIndex = child;
            this.modalPic = true;
        },
        // 获取图片信息
        getPic (pc) {
            this.$nextTick(() => {
                this.footConfig[this.itemIndex].imgList[this.itemChildIndex] = pc.att_dir;
                this.modalPic = false;
                this.$store.commit('admin/mobildConfig/footUpdata',this.footConfig)
            })
        },
        // 添加模块
        addMenu(){
            let obj = {
                imgList:['',''],
                name:'自定义',
                link:''
            }
            this.footConfig.push(obj)
        },
        deleteMenu(index){
            this.$Modal.confirm({
                title: '提示',
                content: '是否确定删除该菜单',
                onOk: () => {
                    this.footConfig.splice(index,1)
                },
                onCancel: () => {
                }
            });

        }
    }
}
