result:
    /*定义滑块 内阴影+圆角*/
    ::-webkit-scrollbar-thumb{
        -webkit-box-shadow: inset 0 0 6px #ddd;
    }
    ::-webkit-scrollbar {
        width: 4px!important; /*对垂直流动条有效*/
    }
    .newcomers{
        margin 12px 18px;
        background: var(--view-theme);
        &.on{
            background #fff;
            .title{
                color #333333;
                .more{
                    background unset;
                    color #666;
                }
            }
            .list{
                .item{
                    background: var(--view-minorColorT);
                }
            }
        }
        .newCon{
            background-size 100% 100%;
            background-repeat no-repeat;
            border-radius: 8px;
            padding 9px 15px;
            &.on{
                background-image url("../../../assets/images/new-bg.png")!important;
            }
        }
        .title{
            color: #FFFFFF;
            font-size: 15px;
            .more{
                width: 45px;
                height: 18px;
                background: linear-gradient(135deg, #FFCA52 0%, #FE960F 100%);
                border-radius: 9px;
                font-size: 12px;
                color #fff;
                padding-left 4px;
                .iconfont{
                    font-size 12px;
                }
            }
        }
        .list{
            flex-wrap nowrap;
            margin-top 7px;
            overflow: hidden;
            .item{
                width: 81px;
                height: 97px;
                background: #FFFFFF;
                border-radius: 6px;
                padding 6px;
                &~.item{
                    margin-left 6px;
                }
                .money{
                    color: var(--view-priceColor);
                    font-size: 12px;
                    margin-top 3px;
                }
                .pictrue{
                    width: 69px;
                    height: 69px;
                    position relative
                    img{
                        width 100%;
                        height 100%;
                    }
                    .label{
                        position absolute;
                        width: 49px;
                        height: 16px;
                        line-height 16px;
                        background: var(--view-theme);
                        border-radius: 8px;
                        font-size: 12px;
                        color #fff;
                        left:50%;
                        margin-left -24.5px;
                        bottom 1px
                    }
                }
            }
        }
    }
    .default{
        background-color #fff;
        text-align center;
        height 50px;
        line-height 50px;
        border-radius 8px;
    }
    .listB{
        width 100%;
        background-color: #fff;
        border-radius: 6px;
        .item{
            padding-left 12px;
            color #333;
            font-size 12px;
            img{
                width 17px;
                height 17px;
                display block;
            }
            .text{
                width 227px;
                border-bottom: 1px solid #EEEEEE;
                padding 10px 12px 10px 0;
                .iconfont{
                    color #8A8A8A;
                    font-size 12px;
                }
            }
        }
    }
    .bgColor{
        background-color unset!important;
        .top{
            .picTxt{
                .txt{
                    .name{
                        color #333!important;
                    }
                    .phone{
                        color rgba(51, 51, 51, 0.8)!important;
                        background-color rgba(51, 51, 51, 0.13)!important;
                    }
                }
            }
            .news{
                .iconfont{
                    color #333!important;
                }
                .num{
                    background-color var(--view-theme)!important;
                    color #fff!important;
                }
            }
            .iconerweima-xingerenzhongxin{
                color #333!important;
            }
        }
        .center{
            color rgba(51, 51, 51, 0.7)!important;
            .num{
                color #333!important;
            }
        }
    }
    .dotted{
        border:1px dashed #2d8cf0;
        cursor pointer;
    }
    .solid{
        border:1px solid #2d8cf0 !important;
    }
    .c_row-item{
        .slider-box{
            .info{
                font-size 13px;
                color: #999999;
            }
        }
    }
    .bottomB{
        width 271px;
        height 62px;
        background: #343A48;
        border-radius: 8px 8px 0px 0px;
        padding 11px 15px 0 15px;
        margin 18px auto 0 auto;
        color: #BBBBBB;
        font-size 13px;
        z-index: 0;
        position: relative;
        &.on{
            background-image url("../../../assets/images/grade01.png");
            background-repeat no-repeat;
            background-size 100%;
            color #fff;
            padding-left: 38px;
            padding-top 9px;
        }
        .iconfont{
            font-size 11px;
        }
        .vip{
            font-size: 13px;
            font-weight bold;
            color: #F8D5A8;
            img{
                width 18px;
                height 18px;
                display inline-block;
                vertical-align middle;
                margin-right 9px;
            }
        }
    }
    .member{
        background-image url("../../../assets/images/user_vip.png");
        background-repeat no-repeat;
        background-size 100%;
        width 270px;
        height 48px;
        margin -50px auto 0 auto;
        position relative;
        z-index 9;
        margin-bottom 13px;
        padding 8px 10px 0 37px;
        color #AE5A2A;
        font-size 12px;

        &.on{
            background-image url("../../../assets/images/grade01.png");
            color #fff;
        }

        .renew{
            font-size 12px;
            .iconjinru{
                font-size 11px;
            }
        }
    }
    .carousel{
        margin 10px 18px 0 18px;
        .swiperimg{
            width: 100%;
            height: 63px;
            border-radius: 8px;
            img{
                width: 100%;
                height: 100%;
                border-radius: 8px;
            }
        }
    }
    .swiper-pagination-fraction, .swiper-pagination-custom, .swiper-container-horizontal > .swiper-pagination-bullets{
        bottom 2px;
    }
    /deep/.swiper-pagination-bullet{
        width 4px;
        height 4px;
    }
    /deep/.swiper-pagination-bullet-active{
        background #fff;
    }
    .users{
        .left{
            background: #F7F7F7;
            width 310px;
            height 550px;
            overflow-x hidden;
            overflow-y auto
            padding-bottom 1px;
            border-radius 10px;
            margin-right 30px;
            border 1px solid #eee;

            .center{
                width 268px;
                height 66px;
                background-color #fff;
                color #555;
                margin 0 auto;
                text-align center;
                border-radius 7px;
                position relative;
                z-index 9;

                .num{
                    font-size: 15px;
                    font-weight 600;
                    color #333;
                }
                .font{
                    font-size 12px
                }
                .item{
                    &~.item{
                        position relative
                        &:before{
                            position absolute;
                            content '';
                            width 1px;
                            height 14px;
                            background-color rgba(255,255,255,0.3)
                            left:-36px;
                            top:50%;
                            margin-top -7px;
                        }
                    }
                }
            }

            .Tcenter{
                margin-top -25px;
            }

            .header{
                background-color var(--view-theme);
                background-image url("../../../assets/images/user01.png");
                background-size 100%;
                background-repeat no-repeat;
                width 100%;
                height 112px;
                position relative;
                margin-bottom 12px;
                &.on{
                    margin-bottom 4px;
                }
                .top{
                    padding 19px 20px 0 20px;
                    .picTxt{
                        .pictrue{
                            width: 35px;
                            height: 35px;
                            border-radius: 50%;
                            margin-right 10px;
                            img{
                                width 100%;
                                height 100%;
                                border-radius 50%;
                            }
                        }
                        .txt{
                            .name{
                                font-size 12px;
                                color #fff;
                                font-weight 600;
                                img{
                                    width 40px;
                                    height 15px;
                                    margin-left 7px;
                                    vertical-align middle;
                                }
                            }
                            .phone{
                                width: 86px;
                                height: 21px;
                                border-radius: 13px;
                                background-color rgba(16, 55, 72, 0.2);
                                font-size: 11px;
                                color #fff;
                                margin-top 4px;
                                .iconfont{
                                    font-size 11px;
                                }
                            }
                        }
                    }
                    .news{
                        position relative
                        margin-left 10px;
                        .iconfont{
                            font-size 22px;
                            color #fff;
                        }
                        .num{
                            position absolute;
                            width: 14px;
                            height: 14px;
                            background: #FFFFFF;
                            border-radius: 50%;
                            font-size: 9px;
                            color: var(--view-theme);
                            text-align center;
                            line-height 14px;
                            top:3px;
                            right -4px;
                        }
                    }
                    .iconerweima-xingerenzhongxin{
                        font-size 20px;
                        color #fff;
                    }
                }
                .bottom{
                    background-image url("../../../assets/images/member.png");
                    width 304px;
                    height 34px;
                    background-size 100%;
                    background-repeat no-repeat;
                    position absolute;
                    bottom 0;
                    padding 0 19px 0 50px;
                    font-size: 13px;
                    color: #905100;
                    &.on{
                        background-image url("../../../assets/images/grade.png");
                        color #fff;
                    }
                    .renew{
                        font-size 12px;
                        .iconjinru{
                            font-size 11px;
                        }
                    }
                }
            }
            .orderCenter{
                background: #FFFFFF;
                border-radius: 8px;
                margin 0 18px 10px 18px;
                text-align center;
                padding 15px 0;
                &.on{
                    position relative;
                    margin-top 10px;
                }
                .title{
                    padding 0 10px;
                    font-size 13px;
                    color: #282828;
                    font-weight 600px;
                    margin-bottom 2px;
                    .all{
                        font-size 12px;
                        color: #666666;
                        .iconfont{
                            font-size 12px;
                            margin-left 2px;
                        }
                    }
                }
                .list{
                    margin-top 10px;
                    .item{
                        font-size 12px;
                        color: #454545;
                        .iconfont{
                            font-size 20px;
                            color var(--view-theme);
                        }
                    }
                }
                &.service{
                    padding 15px 0 0 0
                    margin-top 10px;
                    .list{
                        .item{
                            width 25%;
                            margin-bottom 10px;
                            .pictrue{
                                width 23px;
                                height 23px;
                                margin 0 auto 8px auto;
								font-size 12px;
                                img{
                                    width 100%;
                                    height 100%;
                                }
                            }
                        }
                    }
                }
            }
        }
        .right{
            width 540px;
            /deep/.ivu-radio-wrapper{
                font-size 13px;
                margin-right 20px;
            }
            .title{
                font-size: 14px;
                color: rgba(0, 0, 0, 0.85);
                position relative;
                font-weight bold;
                &:before{
                    content '';
                    position absolute;
                    width: 2px;
                    height: 14px;
                    background: #1890FF;
                    top:50%;
                    margin-top -7px;
                    left:-8px;
                }
            }
            .c_row-item{
                margin-top 24px;
            }
        }
    }
