    import { getExpress, getIntegralOrderRecord } from '@/api/marketing';
    export default {
        name: 'orderDetails',
        data () {
            return {
                modal2: false,
                modals: false,
                grid: {
                    xl: 8,
                    lg: 8,
                    md: 12,
                    sm: 24,
                    xs: 24
                },
                result: [],
								columns1: [
								        {
								            title: '商品信息',
								            slot: 'product',
								            align: 'center',
								            minWidth: 400
								        },
								        {
								            title: '积分兑换',
								            key: 'price',
								            align: 'center'
								        },
								        {
								            title: '数量',
								            key: 'cart_num',
								            align: 'center'
								        },
								        {
								            title: '小计',
								            align: 'center',
								            render: (h, params) => {
									return h('div', params.row.price * params.row.cart_num);
								}
								        }
								    ],
								    columns2: [
								        {
								            title: '订单ID',
								            key: 'oid',
								            align: 'center',
								            minWidth: 40
								        },
								        {
								            title: '操作记录',
								            key: 'change_message',
								            align: 'center',
								            minWidth: 280
								        },
								        {
								            title: '操作时间',
								            key: 'change_time',
								            align: 'center',
								            minWidth: 100
								        }
								    ],
								    recordData: [],
										activeName:'detail',
										orderData:{}
            }
        },
        props: {
            orderDatalist: Object,
            orderId: Number,
			rowActive: Object,
			openErp:{
				type:Boolean,
				default:false,
			}
        },
				watch: {
					orderDatalist (value) {
						this.orderData = value.orderInfo;
						this.getList(value.orderInfo.id);
					}
				},
        methods: {
            openLogistics () {
                this.getOrderData()
                this.modal2 = true;
            },
            // 获取订单物流信息
            getOrderData () {
                getExpress(this.orderId).then(async res => {
                    this.result = res.data.result;
                }).catch(res => {
                    this.$Message.error(res.msg);
                })
            },
						getList (id) {
						    let data = {
						        id: id,
						        datas: this.page
						    }
						    this.loading = true;
						    getIntegralOrderRecord(data).then(async res => {
						        this.recordData = res.data;
						        this.loading = false;
						    }).catch(res => {
						        this.loading = false;
						        this.$Message.error(res.msg);
						    })
						},
						changeMenu (value) {
						    this.$parent.changeMenu(this.rowActive, value);
						},
						sendOrder () {
						    this.$parent.sendOrder(this.rowActive);
						},
						delivery () {
						    this.$parent.delivery(this.rowActive);
						}
        },
        computed: {
        }
    }
