.search-p {
  padding-right: 20px;

  >>> .el-input-group__append {
    top: 0;
    background-color: transparent;
    border-left: none;
    text-align: center;
  }
}

::-webkit-scrollbar-thumb {
  -webkit-box-shadow: inset 0 0 6px #ddd;
  display: none;
}

.tree-box:hover::-webkit-scrollbar-thumb, .select-box:hover::-webkit-scrollbar-thumb {
  display: block;
}

::-webkit-scrollbar {
  width: 3px !important; /* 对垂直流动条有效 */
}

/deep/.el-dialog__body {
  height: 420px;
  padding: 5px 10px 15px 20px !important;
}

/deep/.el-tree-node:focus > .el-tree-node__content {
  background: unset !important;
}

/deep/.el-tree-node__expand-icon {
  margin-top: 9px;
}

/deep/.el-tree--highlight-current .el-tree-node.is-current > .el-tree-node__content {
  background: transparent !important;
}

/deep/.el-tree-node__content:hover {
  background: unset !important;
}

/deep/.el-tree-node__content:active {
  background: unset !important;
}

/deep/.flex-box:hover {
  background: #f5f7fa;
}

/deep/.el-tree-node__content {
  height: auto;
  align-items: start;
}

/deep/.el-tree--highlight-current .el-tree-node.is-current > .el-tree-node__content .flex-box {
  color: #1890ff;
}

/deep/.el-tree-node__content {
  padding-right: 15px;
  border-right: 2px solid transparent;
}

/deep/.el-tree-node > .el-tree-node__children {
  overflow: inherit;
}

.custom-tree-node {
  position: relative;
  width: 100%;

  .right-icon {
    display: none;
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
  }

  .edit-box {
    z-index: 200;
    position: absolute;
    right: 0;
    bottom: -124px;
    width: 120px;
    padding: 7px 0;
    box-shadow: 0px 0px 8px 0px rgba(0, 0, 0, 0.06);
    border-radius: 6px;
    color: #000000;
    background: #fff;

    .edit-item {
      height: 40px;
      line-height: 40px;
      padding-left: 19px;
      font-size: 13px;

      &:active {
        background: #f5f5f5;
      }
    }
  }
}

.tree-icon {
  margin-right: 5px;
  font-size: 18px;
  color: #ffca28;
}

.flex-box {
  display: flex;
  align-items: center;
  height: 40px;

  &.active {
    color: #1890ff;
  }
}

.user-list {
  padding-left: 25px;

  .user-item {
    display: flex;
    align-items: center;
    font-size: 14px;
    padding: 12px 0;

    &:hover {
      background: #f5f7fa;
    }

    img {
      width: 15px;
      height: 15px;
      margin-right: 7px;
      border-radius: 50%;
    }
  }
}

.container {
  display: flex;
  height: 100%;

  .tree-box {
    flex: 1;
    padding-right: 20px;
    overflow-y: auto;
    height: 100%;
  }

  .select-box {
    overflow-y: auto;
    height: 100%;
    width: 340px;
    padding-left: 20px;
    padding-top: 6px;
    border-left: 1px solid #eeeeee;
    padding-right: 10px;

    .title {
      color: rgba(0, 0, 0, 0.6);
      font-size: 13px;
    }

    .list-box {
      .select-item {
        display: flex;
        justify-content: space-between;
        margin-top: 18px;
        cursor: pointer;

        .left {
          display: flex;
        }

        img {
          width: 16px;
          height: 16px;
          margin-right: 5px;
          border-radius: 50%;
        }

        .el-icon-s-cooperation {
          margin-top: -2px;
        }

        .right-box {
          display: flex;
          align-items: center;
          justify-content: space-between;
          font-size: 12px;

          .el-icon-close {
            /* margin-left: 30px; */
            font-size: 16px;
          }
        }
      }
    }
  }
}

.on {
  color: #1890ff;
}
