import { HappyScroll } from 'vue-happy-scroll'
import { productInfoApi } from '@/api/product'
export default {
    name: 'goods_detail',
    props: {
        goodsId: {
            type: String | Number,
            default: ''
        }
    },
    components: {
        HappyScroll
    },
    data () {
        return {
            value2: 0,
            goodsInfo: { productInfo: { slider_image: [] } }
        }
    },
    mounted () {
        this.getInfo()
    },
    methods: {
        getInfo () {
            productInfoApi(this.goodsId).then(res => {
                this.goodsInfo = res.data
            })
        }
    }
}
