import { mapState, mapMutations } from 'vuex'
export default {
    name: 'wechat_live',
    cname: '小程序直播',
    configName: 'c_wechat_live',
    type: 1, // 0 基础组件 1 营销组件 2工具组件
    defaultName: 'liveBroadcast', // 外面匹配名称
    icon: 'iconxiaochengxuzhibo1',
    props: {
        index: {
            type: null,
            default: -1
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'liveBroadcast',
                timestamp: this.num,
                setUp: {
                    tabVal: 0
                },
                bg: {
                    title: '背景色',
                    name: 'bg',
                    default: [{
                        item: '#fff'
                    }],
                    color: [
                        {
                            item: '#fff'
                        }
                    ]
                },
                boxShadow: {
                    title: '阴影颜色',
                    name: 'playBg',
                    default: [{
                        item: 'rgba(0, 0, 0, 0.06)'
                    }],
                    color: [
                        {
                            item: 'rgba(0, 0, 0, 0.06)'
                        }
                    ]
                },
                limit:{
                    title:'显示个数',
                    val: 4,
                },
                listStyle: {
                    title: '列表样式',
                    name: 'listStyle',
                    type: 0,
                    list: [
                        {
                            val: '单列',
                            icon: 'iconPic_big'
                        },
                        {
                            val: '双列',
                            icon: 'iconPic_small'
                        },
                        {
                            val: '大图',
                            icon: 'iconbanner_1'
                        }
                    ]
                },
                // 页面间距
                mbConfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                }
            },
            live: [
                {
                    title: '直播中',
                    name: 'playBg',
                    type: 2,
                    color: '',
                    icon: 'iconzhibozhong',
                    goods: []
                },
                {
                    title: '回放',
                    name: 'endBg',
                    type: 0,
                    color: '',
                    icon: 'iconyijieshu',
                    goods: [
                        {
                            img: 'http://admin.crmeb.net/uploads/attach/2020/05/20200515/4f17d0727e277eb86ecc6296e96c2c09.png',
                            price: '199'
                        },
                        {
                            img: 'http://admin.crmeb.net/uploads/attach/2020/05/20200515/4f17d0727e277eb86ecc6296e96c2c09.png',
                            price: '199'
                        },
                        {
                            img: 'http://admin.crmeb.net/uploads/attach/2020/05/20200515/4f17d0727e277eb86ecc6296e96c2c09.png',
                            price: '199'
                        }
                    ]
                },
                {
                    title: '预告',
                    name: 'notBg',
                    type: 1,
                    color: '',
                    icon: 'iconweikaishi',
                    goods: [
                        {
                            img: 'http://admin.crmeb.net/uploads/attach/2020/05/20200515/4f17d0727e277eb86ecc6296e96c2c09.png',
                            price: '199'
                        },
                        {
                            img: 'http://admin.crmeb.net/uploads/attach/2020/05/20200515/4f17d0727e277eb86ecc6296e96c2c09.png',
                            price: '199'
                        }
                    ]
                }
            ],
            cSlider: '',
            confObj: {},
            pageData: {},
            listStyle: 0,
            bg:'',
            boxShadow:''
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbConfig){
                this.cSlider = data.mbConfig.val;
                this.listStyle = data.listStyle.type
            }
        }
    }
}
