import { mapState } from 'vuex';
export default {
    name: 'attribute',
    props: {
        attrTemplate: {
            type: Boolean
        }
    },
    data () {
        return {
            val: false,
            specsVal: '',
            specs: [],
            attrVal: '',
            attrList: []
        }
    },
    watch: {
        attrTemplate: function (n) {
            this.val = n
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 70;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    methods: {
        cancel () {
            this.$emit('changeTemplate', false)
        },
        confirm () {
            if (this.specsVal === '') {
                this.$Message.error('请填写规格名称');
            } else {
                this.specs.push(this.specsVal);
                this.attrList.push({
                    attr: this.specsVal,
                    inputVal: '',
                    attrVal: []
                });
                this.specsVal = '';
                if (this.specsVal !== '') {
                    this.attrList.forEach(item => {
                        if (item.attrVal.length < 1) {
                            this.$Message.error('请填写规格属性');
                        }
                    })
                }
            }

        },
        confirmAttr (index) {
            let attrList = this.attrList[index];
            if (attrList.inputVal === '') {
                this.$Message.error('请填写规格属性');
            } else {
                attrList.attrVal.push(attrList.inputVal);
                attrList.inputVal === '';
            }
        }
    },
    mounted () {}
}
