import { mapState, mapMutations } from 'vuex'
export default {
    name: 'home_userInfor',
    cname: '用户信息',
    configName: 'c_userInfor',
    icon: 'iconyonghuxinxi',
    type:0,// 0 基础组件 1 营销组件 2工具组件
    defaultName:'userInfor', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'userInfor',
                timestamp: this.num,
                setUp: {
                    tabVal: 0
                },
                checkboxInfo: {
                    title: '资产信息',
                    name: 'checkboxInfo',
                    type:[0,1,2,3],
                    list: [
                        {
                            id:0,
                            name:'优惠券'
                        },
                        {
                            id:1,
                            name:'积分'
                        },
                        {
                            id:2,
                            name:'余额'
                        },
                        {
                            id:3,
                            name:'会员码'
                        }
                    ]
                },
                bgColor: {
                    title: '背景颜色',
                    name: 'bgColor',
                    default: [{
                        item: '#fff'
                    }],
                    color: [
                        {
                            item: '#fff'
                        }
                    ]
                },
                itemStyle: {
                    title: '背景样式',
                    name: 'itemStyle',
                    type: 0,
                    list: [
                        {
                            val: '圆角',
                            icon: 'iconPic_fillet'
                        },
                        {
                            val: '直角',
                            icon: 'iconPic_square'
                        }
                    ]
                },
                prConfig: {
                    title: '背景边距',
                    val: 0,
                    min: 0
                },
                mbCongfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                }
            },
            bgColor: '',
            mTop: '',
            prConfig:0,
            pageData: {},
            itemStyle: 0,
            checkType:-1
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData);
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbCongfig){
                this.bgColor = data.bgColor.color[0].item;
                this.mTop = data.mbCongfig.val;
                this.prConfig = data.prConfig.val;
                this.itemStyle = data.itemStyle.type;
                this.checkType = data.checkboxInfo.type;
            }
        }
    }
}
