import { distributionInfo } from '@/api/supplier'
import { distributionOrder } from '@/api/order'
import QRCode from 'qrcodejs2';
import Setting from '@/setting';
export default {
  data (){
    return {
      columns:[
        {
          title: '商品编号',
          key: 'index',
          align: 'center',
          width: 60
        },
        {
          title: '商品名称',
          slot: 'store_name',
          align: 'center',
          width: 253
        },
        {
          title: '商品规格',
          slot: 'suk',
          align: 'center',
          width: 219
        },
        // {
        //     title: '商品条码',
        //     key: 'bar_code',
        //     align: 'center',
        //     width: 109
        // },
        // {
        //     title: '商品编码',
        //     key: 'code',
        //     align: 'center',
        //     width: 109
        // },
        {
          title: '单价',
          key: 'truePrice',
          align: 'center',
          width: 100
        },
        {
          title: '数量',
          key: 'cart_num',
          align: 'center',
          width: 60
        },
        {
          title: '金额',
          key: 'subtotal',
          align: 'center',
          width: 100
        },
      ],
      data:{},
      goods:[],
      BaseURL: Setting.apiBaseURL.replace(/adminapi/, ""),
      newArrayData:[]
    }
  },
  created() {
    this.getDistribution();
  },
  mounted() {
    this.$nextTick(function () {
      let that = this;
      setTimeout(function () {
        that.creatQrCode();
      },200)
    })
  },
  methods:{
    // 生成二维码
    creatQrCode() {
      let url= this.BaseURL;
      let qrcode = '';
      this.newArrayData.forEach((item,index)=>{
        let obj = document.getElementById('qrCodeUrl'+index);
        qrcode = new QRCode(obj, {
          text: url, // 需要转换为二维码的内容
          width: 90,
          height: 90,
          colorDark: '#000000',
          colorLight: '#ffffff',
          correctLevel: QRCode.CorrectLevel.H
        })
      });

    },
    getDistribution(){
      let header = this.$route.query.status == 1?distributionInfo:distributionOrder;
      header(this.$route.query.id).then(res=>{
        this.data = res.data;
        this.goods = res.data.list
        let data = res.data;
        let newArray = [];
        data.forEach((i,indexi)=>{
          let listArray = [];
          let goods = i.list;
          let count = Math.ceil(goods.length / 7);
          for(let j = 0; j < count; j++){
            let list = goods.slice(j * 7, j * 7 + 7);
            if (list.length){
              listArray.push(list)
            }
          }
          let num = 7 - listArray[listArray.length-1].length;
          if(num){
            for(let z = 0; z < num; z++){
              listArray[listArray.length-1].push({
                cart_num:'',
                index:'',
                store_name:'',
                subtotal:'',
                suk:'',
                truePrice:''
              })
            }
          }
          listArray.forEach((x)=>{
            newArray.push({
              real_name : i.real_name,
              user_address : i.user_address,
              user_phone : i.user_phone,
              freight_price : i.freight_price,
              coupon_price : i.coupon_price,
              vip_true_price : i.vip_true_price,
              use_integral : i.use_integral,
              pay_price : i.pay_price,
              mark : i.mark,
              order_id: i.order_id,
              pay_time: i.pay_time,
              pay_type_name: i.pay_type_name,
              goodsList : x
            });
          })
        })
        this.newArrayData = newArray;
      }).catch(err=>{
        this.$Message.error(err.msg)
      })
    }
  }
}
