import { mapState } from "vuex";
import {
  liveGoods,
  liveSyncGoods,
  liveGoodsDetail,
  liveGoodsShow,
} from "@/api/live";
import goodsFrom from "./components/goods_detail";
export default {
  name: "live",
  components: {
    goodsFrom,
  },
  data() {
    return {
      grid: {
        xl: 7,
        lg: 7,
        md: 12,
        sm: 24,
        xs: 24,
      },
      formValidate: {
        status: "",
        kerword: "",
        page: 1,
        limit: 20,
      },
      treeData: {
        withdrawal: [
          {
            title: "全部",
            value: "",
          },
          {
            title: "待审核",
            value: 0,
          },
          {
            title: "已通过",
            value: 1,
          },
          {
            title: "未通过",
            value: -1,
          },
        ],
      },
      columns1: [
        { key: "product_id", title: "商品ID", width: 80 },
        { slot: "name", minWidth: 240, title: "商品名称" },
        { key: "price", minWidth: 90, title: "直播价" },
        { slot: "cost_price", minWidth: 90, title: "原价" },
        { slot: "stock", minWidth: 90, title: "库存" },
        { slot: "status", minWidth: 90, title: "审核状态" },
        { slot: "is_mer_show", title: "是否显示", minWidth: 90 },
        // {"key": "sort", "title": "排序", "minWidth": 35},
        { slot: "action", title: "操作", minWidth: 120 },
      ],
      tabList: [],
      loading: false,
      modals: false,
      total: 0,
    };
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
  },
  mounted() {
    this.getList();
  },
  methods: {
    // 分页
    pageChange(index) {
      this.formValidate.page = index;
      this.getList();
    },
    // 直播间显示隐藏
    onchangeIsShow({ id, is_show }) {
      liveGoodsShow(id, is_show)
        .then((res) => {
          this.$Message.success(res.msg);
        })
        .catch((error) => {
          this.$Message.error(error.msg);
        });
    },
    // 列表数据
    getList() {
      this.loading = true;
      liveGoods(this.formValidate)
        .then((res) => {
          this.total = res.data.count;
          this.tabList = res.data.list;
          this.loading = false;
        })
        .catch((error) => {
          this.$Message.error(error.msg);
          this.loading = false;
        });
    },
    // 选择
    selChange() {
      this.formValidate.page = 1;
      this.getList();
    },
    // 添加商品
    menusAdd() {
      this.$router.push({
        path: "/admin/marketing/live/add_live_goods",
      });
    },
    // 同步商品
    syncGoods() {
      liveSyncGoods()
        .then((res) => {
          this.$Message.success(res.msg);
          this.getList();
        })
        .catch((error) => {
          this.$Message.error(res.msg);
        });
    },
    edit(row) {
      this.modals = true;
      this.$refs.goodsDetail.getData(row.id);
    },
    // 删除
    del(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `live/goods/del/${row.id}`,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          this.tabList.splice(num, 1);

          this.getList();
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
  },
};
