import { mapState, mapMutations } from 'vuex'
export default {
    name: 'home_short_video',
    cname: '短视频',
    configName: 'c_short_video',
    icon: 'iconduanshipin',
    type:1,// 0 基础组件 1 营销组件 2工具组件
    defaultName:'shortVideo', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'shortVideo',
                timestamp: this.num,
                setUp: {
                    tabVal: 0
                },
                numConfig: {
                    val: 0,
                    title:'显示个数'
                },
                videoList: [],
                itemStyle: {
                    title: '视频样式',
                    name: 'itemStyle',
                    type: 0,
                    list: [
                        {
                            val: '列表模式',
                            icon: 'iconshipinyangshi1'
                        },
                        {
                            val: '图片模式',
                            icon: 'iconshipinyangshi2'
                        }
                    ]
                },
                bgColor: {
                    title: '背景颜色',
                    name: 'bgColor',
                    default: [{
                        item: '#fff'
                    }],
                    color: [
                        {
                            item: '#fff'
                        }
                    ]
                },
                prConfig: {
                    title: '背景边距',
                    val: 15,
                    min: 0
                },
                mbCongfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                }
            },
            bgColor: '',
            mTop: '',
            prConfig:0,
            pageData: {},
            itemStyle: 0,
            videoList: []
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData);
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbCongfig){
                this.bgColor = data.bgColor.color[0].item;
                this.mTop = data.mbCongfig.val;
                this.prConfig = data.prConfig.val;
                this.itemStyle = data.itemStyle.type;
                let videoList = data.videoList || [];
                if(videoList.length){
                    this.videoList = videoList
                }else {
                    this.videoList = [
                        {
                            image:'',
                            like_num:120,
                            type_image:'',
                            type_name:'众邦科技',
                            desc:'观看视频crmeb更多好礼等你来抢，每天都有哟～ 更多好礼请联…'
                        },
                        {
                            image:'',
                            like_num:120,
                            type_image:'',
                            type_name:'众邦科技',
                            desc:'观看视频crmeb更多好礼等你来抢，每天都有哟～ 更多好礼请联…'
                        }
                    ]
                }
            }
        }
    }
}
