import WangEditor from "@/components/wangEditor/index.vue";
import { memberAgreement, memberAgreementSave } from "@/api/user";
import { mapState,mapMutations } from "vuex";
export default {
    components: { WangEditor },
    data() {
        return {
            ueConfig: {
                autoHeightEnabled: false,
                initialFrameHeight: 500,
                initialFrameWidth: "100%",
                UEDITOR_HOME_URL: "/admin/UEditor/",
                serverUrl: ""
            },
            id: 0,
            agreement: {
                title: "",
                content: "",
                status: 1
            },
            content:'',
            spinShow:false
        };
    },
    created() {
        this.memberAgreement();
    },
	computed: {
	  ...mapState("admin/layout", ["isMobile","menuCollapse"]),
	  labelWidth() {
	    return this.isMobile ? undefined : 120;
	  },
	  labelPosition() {
	    return this.isMobile ? "top" : "right";
	  },
	  labelBottom() {
	    return this.isMobile ? undefined : 15;
	  }
	},
    methods: {
		getEditorContent(data){
		    this.agreement.content = data
		},
        memberAgreement() {
            this.spinShow = true;
            memberAgreement()
                .then(res => {
                    this.spinShow = false;
                    const { title, content, status, id } = res.data;
                    this.agreement.title = title;
                    this.agreement.content = content;
                    this.content = content;
                    this.agreement.status = status;
                    this.id = id;
                })
                .catch(err => {
                    this.$Message.error(err);
                    this.spinShow = false;
                });
        },
        // 保存
        memberAgreementSave() {
            this.$Spin.show();
            memberAgreementSave(this.id, this.agreement)
                .then(res => {
                    this.$Spin.hide();
                    this.$Message.success("保存成功");
                    this.memberAgreement();
                })
                .catch(err => {
                    this.$Spin.hide();
                    this.$Message.error(err.msg);
                });
        }
    }
};
