    import { mapState } from 'vuex';
    import { stairListApi } from '@/api/agent';
    import timeOptions from "@/utils/timeOptions";
    export default {
        name: 'promotersList',
        data () {
            return {
                modals: false,
                options: timeOptions,
                fromList: {
                    title: '选择时间',
                    custom: true,
                    fromTxt: [
                        { text: '全部', val: '' },
                        { text: '今天', val: 'today' },
                        { text: '昨天', val: 'yesterday' },
                        { text: '最近7天', val: 'lately7' },
                        { text: '最近30天', val: 'lately30' },
                        { text: '本月', val: 'month' },
                        { text: '本年', val: 'year' }
                    ],
                    fromTxt2: [
                        { text: '全部', val: 0 },
                        { text: '一级推广人', val: 1 },
                        { text: '二级推广人', val: 2 }
                    ]
                },
                formValidate: {
                    limit: 15,
                    page: 1,
                    nickname: '',
                    data: '',
                    type: '',
                    order_id: '',
                    uid: 0
                },
                loading: false,
                tabList: [],
                total: 0,
                timeVal: [],
                columns4: [],
                listTitle: ''
            }
        },
        computed: {
            ...mapState('admin/layout', [
                'isMobile'
            ]),
            labelWidth () {
                return this.isMobile ? undefined : 100;
            },
            labelPosition () {
                return this.isMobile ? 'top' : 'right';
            }
        },
        methods: {
            onCancel () {
                this.formValidate = {
                    limit: 15,
                    page: 1,
                    nickname: '',
                    data: '',
                    type: '',
                    order_id: '',
                    uid: 0
                };
				this.timeVal = [];
            },
            // 具体日期
            onchangeTime (e) {
                this.timeVal = e;
                this.formValidate.data = this.timeVal.join('-');
            },
            // 列表
            getList (row, tit) {
                this.listTitle = tit;
                this.rowsList = row;
                this.loading = true;
                let url = '';
                if (this.listTitle === 'man') {
                    url = 'agent/stair';
                } else {
                    url = 'agent/stair/order';
                }
                this.formValidate.uid = row.uid
                stairListApi(url, this.formValidate).then(async res => {
                    let data = res.data
                    this.tabList = data.list;
                    this.total = data.count;
                    if (this.listTitle === 'man') {
                        this.columns4 = [
                            {
                                title: 'UID',
                                minWidth: 80,
                                key: 'uid'
                            },
                            {
                                title: '头像',
                                key: 'avatar',
                                minWidth: 80,
                                render: (h, params) => {
                                    return h('viewer', [
                                        h('div', {
                                            style: {
                                                width: '36px',
                                                height: '36px',
                                                borderRadius: '4px',
                                                cursor: 'pointer'
                                        } }, [
                                            h('img', {
                                                attrs: {
                                                    src: params.row.avatar ? params.row.avatar : require('../../../assets/images/moren.jpg')
                                                },
                                                style: {
                                                    width: '100%',
                                                    height: '100%'
                                                }
                                            })
                                        ])
                                    ]);
                                }
                            },
                            {
                                title: '用户信息',
                                key: 'nickname',
                                minWidth: 120
                            },
                            {
                                title: '是否推广员',
                                key: 'promoter_name',
                                minWidth: 100
                            },
                            {
                                title: '推广人数',
                                key: 'spread_count',
                                sortable: true,
                                minWidth: 90
                            },
                            {
                                title: '订单数',
                                key: 'order_count',
                                sortable: true,
                                minWidth: 90
                            },
                            {
                                title: '关注时间',
                                key: 'add_time',
                                sortable: true,
                                minWidth: 130
                            }
                        ];
                    } else {
                        this.columns4 = [
                            {
                                title: '订单ID',
                                key: 'order_id'
                            },
                            {
                                title: '用户信息',
                                key: 'user_info'
                            },
                            {
                                title: '时间',
                                key: '_add_time'
                            },
                            {
                                title: '返佣金额',
                                key: 'brokerage_price',
                                render: (h, params) => {
                                    return h('viewer', [
                                        h('span', params.row.brokerage_price || 0)
                                    ]);
                                }
                            }
                        ];
                    }
                    this.loading = false;
                }).catch(res => {
                    this.loading = false;
                    this.tabList = [];
                    this.$Message.error(res.msg);
                })
            },
            pageChange () {
                this.formValidate.page = 1;
                this.getList(this.rowsList, this.listTitle);
            },
            // 搜索
            userSearchs () {
				this.formValidate.page = 1;
                this.getList(this.rowsList, this.listTitle);
            }
        }
    }
