	}
	.pictrue {
	  width: 85px;
	  height: 85px;
	  // border: 1px dotted rgba(0, 0, 0, 0.1);
	  display: inline-block;
	  position: relative;
	  cursor: pointer;

	  img {
	    width: 100%;
	    height: 100%;
	  }
	}

	.pictrueTab {
		// margin-top 20px;
	  width: 60px !important;
	  height: 60px !important;
      border 1px dashed #DDDDDD;
      border-radius 4px;
      background-color rgba(0, 0, 0, 0.02);
      line-height 60px;
      text-align center;
	}

    .card_box{
		.item_box{
			width 320px;
			// height 536px;
			margin 0 auto;
			margin-bottom 30px;
			overflow hidden;
			// background-color #f6f6f8;
			border 1px solid #e7e7eb;
			img{
				width 320px
				display inline-block
				margin 5px 0px;

			}
			.legal{
				width 266px;
				margin 0px auto;
				margin-bottom 20px;
				display flex;
				justify-content space-around;
				text-align center;
				// padding 0px 20px;
				.legal_box{
					cursor pointer;
					.legal_top{
						color #9a9a9a;
						font-size 12px;
					}
					.legal_bottom{
						color #63b359;
						font-size 15px;
					}
				}
			}
			.entry{
				// width 266px;
				margin 20px auto;
				// margin-bottom 20px;
				text-align center
				.entry_btn{
					width: fit-content;
					padding 0px 30px;
					height 40px;
					line-height 40px;
					margin 0px auto;
					border 1px solid #63b359;
					color #63b359;
					font-weight 500;
					font-size 14px;
					margin-bottom 10px;
					border-radius 5px;
					cursor pointer;
				}
				.entry_tit{
					color #9a9a9a;
					font-size 12px;
				}
			}
			.card{
				width 266px;
				height 147px;
				margin 20px auto 10px;
				border-radius 10px;
				padding 20px;
				color #FFFFFF;
				background-repeat no-repeat !important;
				background-size: 100% 100% !important;

				.top{
					height 80px;
					display flex;
					justify-content space-between;
					img{
						width 36px;
						height 36px;
						border 1px solid #FFFFFF;
						border-radius 5px
					}
					.user{
						display flex;
						justify-content space-between;
						img{
							width 40px;
							height 40px;
							border 1px solid #FFFFFF;
							border-radius 50%
							overflow hidden;
							background-color #FFFFFF;
						}
						.info_box{
							margin-left 5px
							font-weight bold;
							margin-top 5px
							.info{
								max-width 150px;
								font-size 15px;
								overflow hidden;
								text-overflow ellipsis;
								white-space nowrap;
							}
						}
					}
				}
				.bottom{
					display flex;
					justify-content space-between;
					font-size 18px
				}
			}
			.card_link{
				padding 0px 5px;
				.link{
					height 40px;
					line-height 40px;
					border-bottom 1px solid #e7e7eb;

				}
				.link:nth-last-child(1){
					border none
				}
			}
			.custom_card{
				height 222px;
				background-color #f6f6f8;
				padding 15px 0px 50px;
				.custom_link{
					background-color #FFFFFF
					padding 0px 5px;
					.link{
						height 40px;
						line-height 40px
						border-bottom 1px solid #e7e7eb;
					}
					.link:nth-last-child(1){
						border none
					}
				}

			}
		}
	}
	.card_content{
		// border 1px solid #e7e7eb;
		// background-color #f6f6f8;
		.add{
			width 138px;
			height 36px;
			// padding 0px 22px;
			// border 1px solid #e6e9eb;
            border-radius 4px;
			background-color #1890FF;
			margin 30px 0px 29px 30px;
			line-height 36px;
            text-align center;
			cursor pointer;
            color #FFFFFF;
		}
		.content_box{
			// height 700px;
			padding 20px 20px;
			.addentrance{
				padding-bottom 20px;
                padding-left 30px;
				// border-bottom 1px solid #e6e9eb;
                .title_text{
                    .left{
                        width 95px;
                    }
                }
			}
			.add_bar{
				margin-top 30px;
				display flex;
				justify-content space-between;
                align-items center;
                height 34px;
                padding 0 20px 0 15px;
                background-color #F5F7FA;
				.del{
					color #F5222D;
					cursor pointer;
				}
			}
			.title_bar{
				// height 30px;
                padding-left 10px;
                border-left 2px solid #1890FF;
                font-weight bold;
				line-height 15px;
                color #000000;
				// border-bottom 1px solid #e6e9eb;
                span{
                    margin-left 6px;
                    font-weight normal;
                    font-size 12px;
                    color #999999;
                }
			}
			.title_text{
				display flex;
				justify-content start;
                align-items center;
				margin-top 26px;
				.left{
					text-align right ;
					width 114px;
                    padding-right 6px;
                    color rgba(0, 0, 0, 0.85);
				}
                &.required{
                    .left::before{
                        content: "*";
                        padding-right 5px;
                        vertical-align middle;
                        color #F56C6C;
                    }
                }
				.right{
					display flex;
					justify-content start;
					.tit{
						display inline-block;
						margin-top 10px;
					}
					.legaladd{
						width 100px;
						height 30px;
						padding 0px 15px;
						text-align center;
						border 1px solid #e6e9eb;
						background-color #FFFFFF;
						line-height 30px;
						cursor pointer;
					}
					img{
						width 80px;
						height 80px;
					}
					.img-boxs{
						position relative
						width: 60px;
						height: 60px;
						background: rgba(0, 0, 0, 0);
						border-radius: 6px;
						overflow hidden
						img{
							width 100%
							height 100%
						}
						.img_font{
							position absolute
							bottom 0
							left 0
							width 100%
							height: 24px;
							background: #000000;
							opacity: 0.4;
							border-radius: 0px 0px 6px 6px;
						}
						.img_fonts{
							position absolute
							bottom 0
							left 0
							width 100%
							height: 24px;
							border-radius: 0px 0px 6px 6px;
							color #FFFFFF
							text-align center
							line-height 24px

						}
					}


				}
			}
		}
	}
