import { getByCategory } from '@/api/diy'
export default {
    name: 'index',
    props:{},
    data () {
        return {
            formValidate: {
                pid: 0,
                name: ''
            },
            treeSelect: [],
            loading: false,
            grid: {
                xl: 10,
                lg: 10,
                md: 12,
                sm: 24,
                xs: 24
            },
            tableList: [],
            currentid: 0,
            productRow: {},
            columns: [
                {
                    type: 'selection',
                    width: 60,
                    align: 'center'
                },
                {
                    title: '商品ID',
                    key: 'id'
                },
                {
                    title: '图片',
                    slot: 'image'
                },
                {
                    title: '商品分类',
                    key: 'cate_name',
                    minWidth: 150
                }
            ],
            images: [],
        }
    },
    computed: {
    },
    created () {},
    mounted() {
        this.goodsCategory();
        this.getList();
    },
    methods: {
        selectionGood(e) {
            this.$emit('getProductDiy',e)
        },
        // 商品一级分类；
        goodsCategory() {
            getByCategory().then(res => {
                this.treeSelect = res.data;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 列表
        getList() {
            this.loading = true;
            getByCategory(this.formValidate).then(async res => {
                this.tableList = res.data;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })

        },
        // 表格搜索
        userSearchs() {
            this.getList();
        }
        // clear() {
        //     this.productRow.id = '';
        //     this.currentid = '';
        // }
    }
}
