import {
  combinationListApi,
  combinationSetStatusApi,
  storeCombinationApi,
} from "@/api/marketing";
import { mapState } from "vuex";
import { formatDate } from "@/utils/validate";
import exportExcel from "@/utils/newToExcel.js";

export default {
  name: "index",
  filters: {
    formatDate(time) {
      if (time !== 0) {
        let date = new Date(time * 1000);
        return formatDate(date, "yyyy-MM-dd hh:mm");
      }
    },
    filterStatus(val) {
      let nowTime = Date.parse(new Date()) / 1000;
      if (
        val.start_time < nowTime &&
        val.stop_time > nowTime &&
        val.is_show == 1
      ) {
        return "活动进行中";
      } else if (val.stop_time < nowTime && val.status == 1) {
        return "活动已结束";
      } else {
        return "活动未开始";
      }
    },
  },
  data() {
    return {
      loading: false,
      grid: {
        xl: 7,
        lg: 7,
        md: 12,
        sm: 24,
        xs: 24,
      },
      formValidate: {
        start_status: "",
        is_show: "",
        store_name: "",
        page: 1,
        limit: 15,
      },
      value: "",
      columns1: [
        {
          title: "ID",
          key: "id",
          width: 80,
        },
        {
          title: "拼团图片",
          slot: "image",
          minWidth: 90,
        },
        {
          title: "拼团名称",
          slot: "title",
          minWidth: 150,
        },
        {
          title: "原价",
          key: "ot_price",
          minWidth: 100,
        },
        {
          title: "拼团价",
          key: "price",
          minWidth: 120,
        },
        // {
        //     title: '库存',
        //     key: 'stock',
        //     minWidth: 100
        // },
        {
          title: "拼团人数",
          key: "count_people",
          minWidth: 80,
        },
        // {
        //     title: '访问人数',
        //     key: 'count_people_browse',
        //     minWidth: 100
        // },
        // {
        //     title: '展现量',
        //     key: 'browse',
        //     minWidth: 150
        // },
        {
          title: "参与人数",
          key: "count_people_all",
          minWidth: 80,
        },
        {
          title: "成团数量",
          key: "count_people_pink",
          minWidth: 80,
        },
        // {
        //     title: '浏览量',
        //     key: 'browse',
        //     minWidth: 150
        // },
        {
          title: "限量",
          key: "quota_show",
          minWidth: 80,
        },
        {
          title: "限量剩余",
          key: "quota",
          minWidth: 100,
        },
        {
          title: "活动状态",
          slot: "start_name",
          minWidth: 100,
        },
        {
          title: "结束时间",
          slot: "stop_time",
          minWidth: 150,
        },
        {
          title: "状态",
          slot: "is_show",
          minWidth: 120,
        },
        {
          title: "操作",
          slot: "action",
          fixed: "right",
          minWidth: 170,
        },
      ],
      tableList: [],
      total: 0,
      statisticsList: [],
    };
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
  },
  created() {
    this.getList();
  },
  methods: {
    // 导出
    // exports () {
    //     let formValidate = this.formValidate;
    //     let data = {
    //         start_status: formValidate.start_status,
    //         is_show: formValidate.is_show,
    //         store_name: formValidate.store_name
    //     };
    //     storeCombinationApi(data).then(res => {
    //         location.href = res.data[0];
    //     }).catch(res => {
    //         this.$Message.error(res.msg)
    //     })
    // },
    // 数据导出；
    async exports() {
      let [th, filekey, data, fileName] = [[], [], [], ""];
      //   let fileName = "";
      let excelData = JSON.parse(JSON.stringify(this.formValidate));
      excelData.page = 1;
      for (let i = 0; i < excelData.page + 1; i++) {
        let lebData = await this.getExcelData(excelData);
        if (!fileName) fileName = lebData.filename;
        if (!filekey.length) {
          filekey = lebData.filekey;
        }
        if (!th.length) th = lebData.header;
        if (lebData.export.length) {
          data = data.concat(lebData.export);
          excelData.page++;
        } else {
          exportExcel(th, filekey, fileName, data);
          return;
        }
      }
    },
    getExcelData(excelData) {
      return new Promise((resolve, reject) => {
        storeCombinationApi(excelData).then((res) => {
          return resolve(res.data);
        });
      });
    },
    // 添加
    add() {
      this.$router.push({ path: "/admin/marketing/store_combination/create" });
    },
    // 编辑
    edit(row) {
      this.$router.push({
        path: "/admin/marketing/store_combination/create/" + row.id + "/0",
      });
    },
    // 一键复制
    copy(row) {
      this.$router.push({
        path: "/admin/marketing/store_combination/create/" + row.id + "/1",
      });
    },
    // 删除
    del(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `marketing/combination/${row.id}`,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          this.tableList.splice(num, 1);
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 列表
    getList() {
      this.loading = true;
      combinationListApi(this.formValidate)
        .then(async (res) => {
          let data = res.data;
          this.tableList = data.list;
          this.total = res.data.count;
          this.loading = false;
        })
        .catch((res) => {
          this.loading = false;
          this.$Message.error(res.msg);
        });
    },
    pageChange(index) {
      this.formValidate.page = index;
      this.getList();
    },
    // 表格搜索
    userSearchs() {
      this.formValidate.page = 1;
      this.getList();
    },
    // 修改是否显示
    onchangeIsShow(row) {
      let data = {
        id: row.id,
        status: row.is_show,
      };
      combinationSetStatusApi(data)
        .then(async (res) => {
          this.$Message.success(res.msg);
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
  },
};
