import exportExcel from "@/utils/newToExcel.js";
import commissionDetails from "../components/commissionDetails.vue";
import {
  storeFfundRecordApi,
  exportfundRecordApi,
  staffListInfo,
} from "@/api/store";
import timeOptions from "@/utils/timeOptions";
export default {
  name: "bill",
  components: {
    commissionDetails,
  },
  data() {
    return {
      grid: {
        xl: 7,
        lg: 7,
        md: 12,
        sm: 24,
        xs: 24,
      },
      total: 0,
      loading: false,
      tab: "day",
      staff: [],
      columns: [
        {
          title: "ID",
          key: "id",
          width: 60,
        },
        {
          title: "标题",
          key: "title",
          minWidth: 80,
        },
        {
          title: "日期",
          key: "add_time",
          minWidth: 80,
        },
        {
          title: "收入金额",
          slot: "income_num",
          minWidth: 80,
        },
        {
          title: "支出金额",
          slot: "exp_num",
          minWidth: 80,
        },
        {
          title: "门店应入账金额",
          slot: "entry_num",
          minWidth: 80,
        },
        {
          title: "操作",
          slot: "action",
          fixed: "right",
          minWidth: 120,
          align: "center",
        },
      ],
      orderList: [
        {
          id: "1",
          order_id: "200",
          pay_price: "200",
          status: 1,
          phone: "13000000000",
          address: "100",
        },
      ],
      formValidate: {
        store_id: "",
        data: "",
        page: 1,
        limit: 15,
      },
      timeVal: [],
      options: timeOptions,
    };
  },
  computed: {
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
  },
  mounted() {
    this.onClickTab(this.tab);
    this.staffApi();
  },
  methods: {
    staffApi() {
      staffListInfo().then((res) => {
        this.staff = res.data;
      });
    },
    onClickTab(e) {
      this.tab = e;
      this.getList();
    },
    search() {
      this.formValidate.page = 1;
      this.getList();
    },
    getList() {
      this.loading = true;
      let data = {
        timeType: this.tab,
        data: this.formValidate.data,
        page: this.formValidate.page,
        limit: this.formValidate.limit,
        store_id: this.formValidate.store_id,
      };
      storeFfundRecordApi(data).then((res) => {
        this.orderList = res.data.list;
        this.loading = false;
        this.total = res.data.count;
      });
    },
    // 选择时间
    selectChange(tab) {
      this.formValidate.page = 1;
      this.formValidate.data = tab;
      this.timeVal = [];
      this.getList();
    },
    // 具体日期
    onchangeTime(e) {
      this.timeVal = e;
      this.formValidate.data = this.timeVal[0] ? this.timeVal.join("-") : "";
      this.formValidate.page = 1;
    },
    //分页
    pageChange(status) {
      this.formValidate.page = status;
      this.getList();
    },
    // 账单详情
    Info(row) {
      this.$refs.commission.modals = true;
      this.$refs.commission.getList(row.ids);
    },
    //下载
    async download(row) {
      let [th, filekey, data, fileName] = [[], [], [], ""];
      let excelData = {
        ids: row.ids,
        page: 1,
        store_id: this.formValidate.store_id,
      };
      let lebData = await this.getExcelData(excelData);
      if (!fileName) fileName = lebData.filename;
      if (!filekey.length) {
        filekey = lebData.filekey;
      }
      if (!th.length) th = lebData.header;
      data = data.concat(lebData.export);
      exportExcel(th, filekey, fileName, data);
      return;
    },
    getExcelData(excelData) {
      return new Promise((resolve, reject) => {
        exportfundRecordApi(excelData).then((res) => {
          return resolve(res.data);
        });
      });
    },
  },
};
