	import { getOrderRecord } from '@/api/order';
	import {
		getExpress
	} from '@/api/order';
	export default {
		name: 'orderDetails',
        filters: {
            invoiceType: value => value == 1 ? '电子普通发票' : '纸质专用发票',
            invoiceHeaderType: value => value == 1 ? '个人' : '企业'
        },
		data() {
			return {
				modal2: false,
				modals: false,
				grid: {
					xl: 8,
					lg: 8,
					md: 12,
					sm: 24,
					xs: 24
				},
				result: [],
				columns1: [
					{
                        title: '商品信息',
                        slot: 'product',
						align: 'center',
                        minWidth: 400
                    },
                    {
                        title: '价格',
                        key: 'truePrice',
						align: 'center'
                    },
                    {
                        title: '数量',
                        key: 'cart_num',
						align: 'center'
                    },
                    {
                        title: '小计',
						align: 'center',
						render: (h, params) => {
							return h('div', params.row.truePrice * params.row.cart_num);
						}
                    }
				],
				columns2: [
                    {
                        title: '订单ID',
                        key: 'oid',
                        align: 'center',
                        minWidth: 40
                    },
                    {
                        title: '操作记录',
                        key: 'change_message',
                        align: 'center',
                        minWidth: 280
                    },
                    {
                        title: '操作时间',
                        key: 'change_time',
                        align: 'center',
                        minWidth: 100
                    }
                ],
				recordData: [],
				loading: false,
				page: {
                    page: 1, // 当前页
                    limit: 10 // 每页显示条数
                },
				activeName:'detail'				
			}
		},
		props: {
			orderDatalist: Object,
			orderId: Number,
			rowActive: Object,
			currentTab: String,
			openErp:{
				type: Boolean,
				default: false,
			}
		},
		watch: {
			orderId (value) {
				this.getList(value);
			}
		},
		methods: {
			openLogistics() {
				this.getOrderData()
				this.modal2 = true;
			},
			// 获取订单物流信息
			getOrderData() {
				getExpress(this.orderId).then(async res => {
					this.result = res.data.result;
				}).catch(res => {
					this.$Message.error(res.msg);
				})
			},
			getList (id) {
                let data = {
                    id: id,
                    datas: this.page
                }
                this.loading = true;
                getOrderRecord(data).then(async res => {
                    this.recordData = res.data;
                    this.loading = false;
                }).catch(res => {
                    this.loading = false;
                    this.$Message.error(res.msg);
                })
            },
			distribution () {
				this.$parent.distribution(this.rowActive);
			},
			remark () {
				this.$parent.remark(this.rowActive);
			}
		},
		computed: {}
	}
