import { productInfo } from '@/api/kefu'
import { serviceInfo } from '@/api/kefu_mobile'
export default {
    name: "goods_detail",
    props:{
        goodsId:{
            type:String | Number,
            default:''
        }
    },
    data(){
        return {
            value2: 0,
            goodsInfo:{},
            goodsId:'',
            swiperOption:{
                //显示分页
                pagination: {
                    el: '.swiper-pagination'
                },
                //设置点击箭头
                navigation: {
                    nextEl: '.swiper-button-next',
                    prevEl: '.swiper-button-prev'
                },
                //自动轮播
                autoplay: {
                    delay: 2000,
                    //当用户滑动图片后继续自动轮播
                    disableOnInteraction: false,
                },
                //开启循环模式
                loop: true
            }
        }
    },
    created() {
        this.goodsId = this.$route.query.goodsId
        serviceInfo().then(res => {
            window.document.title = `${res.data.site_name} - 商品详情`
        })
    },
    mounted() {
        this.getInfo()
    },
    methods:{
        getInfo(){
            productInfo(this.goodsId).then(res=>{
                this.goodsInfo = res.data
            })
        }
    }
}
