import {
  getExpress,
  getOrderRecord,
  splitOrder,
  remindOrder,
} from '@/api/order'
import template from '../../../setting/devise/template.vue'
export default {
  components: { template },
  name: 'orderDetails',
  filters: {
    invoiceType: (value) => (value == 1 ? '电子普通发票' : '纸质专用发票'),
    invoiceHeaderType: (value) => (value == 1 ? '个人' : '企业'),
  },
  data() {
    return {
      isShow: 0,
      modal2: false,
      modals: false,
      grid: {
        xl: 8,
        lg: 8,
        md: 12,
        sm: 24,
        xs: 24,
      },
      result: [],
      columns1: [
        {
          title: '商品信息',
          slot: 'product',
          minWidth: 400,
        },
        {
          title: '售价',
          key: 'sum_price',
        },
        {
          title: '实付金额',
          key: 'truePrice',
        },
        {
          title: '数量',
          key: 'cart_num',
        },
        {
          title: '小计',
          render: (h, params) => {
            return h(
              'div',
              (params.row.truePrice * params.row.cart_num).toFixed(2)
            )
          },
        },
      ],
      columns2: [
        {
          title: '订单ID',
          key: 'oid',
          minWidth: 40,
        },
        {
          title: '操作记录',
          key: 'change_message',
          minWidth: 280,
        },
        {
          title: '操作时间',
          key: 'change_time',
          minWidth: 100,
        },
      ],
      columnSplit: [
        {
          title: '订单号',
          slot: 'order_id',
          minWidth: 100,
        },
        {
          title: '商品信息',
          slot: 'product',
          minWidth: 250,
        },
        {
          title: '发货信息',
          slot: 'deliveryInfo',
          minWidth: 100,
        },
        {
          title: '操作',
          slot: 'action',
          minWidth: 90,
        },
      ],
      recordData: [],
      activeName: 'detail',
      orderData: {},
      splitList: [],
      logisticsCon: {},
    }
  },
  props: {
    orderDatalist: Object,
    orderId: Number,
    rowActive: Object,
    formType: {
      type: Number,
      default: 0,
    },
    openErp: {
      type: Boolean,
      default: false,
    },
  },
  watch: {
    orderDatalist(value) {
      this.orderData = value.orderInfo
      this.getList(
        !this.formType ? value.orderInfo.store_order_id : value.orderInfo.id
      )
      if (this.formType) {
        this.getSplitOrder(value.orderInfo.id)
      }
    },
    orderData(value) {
      if (value && value.custom_form && value.custom_form.length) {
        value.custom_form.forEach((item) => {
          if (item.value) {
            return (this.isShow = 1)
          }
        })
      }
    },
  },
  mounted() {
    if (
      this.orderData &&
      this.orderData.custom_form &&
      this.orderData.custom_form.length
    ) {
      this.orderData.custom_form.forEach((item) => {
        if (item.value) {
          return (this.isShow = 1)
        }
      })
    }
  },
  methods: {
    openItemLogistics(row) {
      this.logisticsCon = row
      this.modal2 = true
      getExpress(row.id)
        .then(async (res) => {
          this.result = res.data.result
        })
        .catch((res) => {
          this.$Message.error(res.msg)
        })
    },
    openLogistics() {
      this.modal2 = true
      this.getOrderData()
    },

    // 提醒发货
    btnClick(row) {
      let data = {
        supplier_id: row.supplier_id,
        id: row.id,
      }
      remindOrder(data)
        .then(async (res) => {
          this.$Message.success(res.msg)
        })
        .catch((res) => {
          this.$Message.error(res.msg)
        })
    },
    // 获取订单物流信息
    getOrderData() {
      getExpress(
        !this.formType
          ? this.orderDatalist.orderInfo.store_order_id
          : this.orderDatalist.orderInfo.id
      )
        .then(async (res) => {
          this.result = res.data.result
        })
        .catch((res) => {
          this.$Message.error(res.msg)
        })
    },
    //发货记录
    getSplitOrder(id) {
      splitOrder(id, { status: 2 })
        .then((res) => {
          this.splitList = res.data
        })
        .catch((err) => {
          this.$Message.error(err.msg)
        })
    },
    getList(id) {
      let data = {
        id: id,
        datas: this.page,
      }
      this.loading = true
      getOrderRecord(data)
        .then(async (res) => {
          this.recordData = res.data
          this.loading = false
        })
        .catch((res) => {
          this.loading = false
          this.$Message.error(res.msg)
        })
    },
    changeMenu(value, row, name, num) {
      if (num) {
        this.$parent.changeMenu(row, name, num)
      } else {
        this.$parent.changeMenu(this.rowActive, value)
      }
    },
    distribution(row, num) {
      if (num) {
        this.$parent.distribution(row)
      } else {
        this.$parent.distribution(this.rowActive)
      }
    },
    edit(row, num) {
      if (num) {
        this.$parent.edit(row)
      } else {
        this.$parent.edit(this.rowActive)
      }
    },
    sendOrder(row, num) {
      if (num) {
        this.$parent.sendOrder(row, num)
      } else {
        this.$parent.sendOrder(this.rowActive)
      }
    },
    delivery(row, num) {
      if (num) {
        this.$parent.delivery(row, num)
      } else {
        this.$parent.delivery(this.rowActive)
      }
    },
    bindWrite(row, num) {
      if (num) {
        this.$parent.bindWrite(row)
      } else {
        this.$parent.bindWrite(this.rowActive)
      }
    },
  },
  computed: {},
}
