import { mapState } from 'vuex'
export default {
    name: 'home_pink',
    cname: '拼团',
    icon: 'iconpintuan1',
    configName: 'c_home_pink',
    type:1,// 0 基础组件 1 营销组件 2工具组件
    defaultName:'combination', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)

            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'combination',
                timestamp: this.num,
                setUp: {
                    tabVal: 0
                },
                numConfig: {
                    val: 3
                },
                priceShow: {
                    title: '是否显示价格',
                    val: true
                },
                bntShow: {
                    title: '是否显示按钮',
                    val: true
                },
                titleShow: {
                    title: '是否显示名称',
                    val: true
                },
                pinkShow: {
                    title: '是否显示拼团标签',
                    val: true
                },
                joinShow: {
                    title: '是否显示参团标签',
                    val: true
                },
                txtColor: {
                    title: '文字背景色',
                    name: 'themeColor',
                    default: [{
                        item: 'rgba(255,68,68,0.1)'
                    }],
                    color: [
                        {
                            item: 'rgba(255,68,68,0.1)'
                        }

                    ]
                },
                fontColor:{
                    title: '价格颜色',
                    name: 'fontColor',
                    default: [
                        {
                            item: '#E93323'
                        }
                    ],
                    color: [
                        {
                            item: '#E93323'
                        }
                    ]
                },
                conStyle: {
                    title: '背景样式',
                    name: 'conStyle',
                    type: 1,
                    list: [
                        {
                            val: '直角',
                            icon: 'iconPic_square'
                        },
                        {
                            val: '圆角',
                            icon: 'iconPic_fillet'
                        }
                    ]
                },
                bgColor: {
                    title: '背景颜色',
                    name: 'themeColor',
                    default: [{
                        item: '#fff'
                    }],
                    color: [
                        {
                            item: '#fff'
                        }
                    ]
                },
                prConfig: {
                    title: '背景边距',
                    val: 10,
                    min: 0
                },
                themeColor: {
                    title: '主题风格',
                    name: 'themeColor',
                    default: [{
                        item: '#E93323'
                    }],
                    color: [
                        {
                            item: '#E93323'
                        }

                    ]
                },
                // 页面间距
                mbConfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                },
                // 左右间距
                lrConfig: {
                    title: '左右边距',
                    val: 10,
                    min: 0
                },
                imgConfig: {
                    title: '最多可添加1张图片，建议宽度18 * 18px',
                    url: 'http://pro.crmeb.net/static/images/group02.gif'
                }
            },
            list: [
                {
                    img: '',
                    name: '小米家用电饭煲',
                    price: '234',
                    num: '1234'
                },
                {
                    img: '',
                    name: '小米家用电饭煲',
                    price: '234',
                    num: '1234'
                },
                {
                    img: '',
                    name: '小米家用电饭煲',
                    price: '234',
                    num: '1234'
                }
            ],
            txtColor: '',
            listRight: '',
            mTop: '',
            txtBg: '',
            pageData: {},
            imgUrl: '',
            priceShow: true,
            bntShow: true,
            titleShow: true,
            pinkShow: true,
            joinShow: true,
            prConfig:0,
            bgColor: '',
            conStyle:1,
            fontColor:''
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbConfig){
                this.fontColor = data.fontColor.color[0].item;
                this.txtColor = data.themeColor.color[0].item;
                this.listRight = data.lrConfig.val;
                this.mTop = data.mbConfig.val;
                this.txtBg = data.txtColor.color[0].item;
                this.imgUrl = data.imgConfig.url;
                this.priceShow = data.priceShow.val;
                this.bntShow = data.bntShow.val;
                this.titleShow = data.titleShow.val;
                this.pinkShow = data.pinkShow.val;
                this.joinShow = data.joinShow.val;
                this.prConfig = data.prConfig.val;
                this.bgColor = data.bgColor.color[0].item;
                this.conStyle = data.conStyle.type;
            }
        }
    }
}
