import { mapState } from 'vuex';
import { couponListApi, couponCreateApi, couponEditeApi, couponSendApi } from '@/api/marketing';
import editFrom from '@/components/from/from';
import { formatDate } from '@/utils/validate';
export default {
    name: 'storeCoupon',
    filters: {
        formatDate (time) {
            if (time !== 0) {
                let date = new Date(time * 1000);
                return formatDate(date, 'yyyy-MM-dd hh:mm');
            }
        }
    },
    components: { editFrom },
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            loading: false,
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '优惠券名称',
                    key: 'title',
                    minWidth: 150
                },
                {
                    title: '优惠券类型',
                    key: 'type',
                    minWidth: 80
                },
                {
                    title: '面值',
                    slot: 'coupon_price',
                    minWidth: 100
                },
                {
                    title: '最低消费额',
                    key: 'use_min_price',
                    minWidth: 100
                },
                {
                    title: '有效期限(天)',
                    key: 'coupon_time',
                    minWidth: 120
                },
                {
                    title: '排序',
                    key: 'sort',
                    minWidth: 80
                },
                {
                    title: '是否有效',
                    slot: 'status',
                    minWidth: 90
                },
                {
                    title: '添加时间',
                    slot: 'add_time',
                    minWidth: 150
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 170
                }
            ],
            tableFrom: {
                status: '',
                title: '',
                page: 1,
                limit: 15
            },
            tableList: [],
            total: 0,
            FromData: null
        }
    },
    created () {
        this.getList();
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 90;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'left';
        }
    },
    methods: {
        // 失效
        couponInvalid (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `marketing/coupon/status/${row.id}`,
                method: 'PUT',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.getList();
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 发布
        couponSend (row) {
            this.$modalForm(couponSendApi(row.id)).then(() => this.getList());
        },
        // 删除
        couponDel (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `marketing/coupon/del/${row.id}`,
                method: 'DELETE',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.tableList.splice(num, 1)
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 列表
        getList () {
            this.loading = true;
            this.tableFrom.status = this.tableFrom.status || '';
            couponListApi(this.tableFrom).then(async res => {
                let data = res.data
                this.tableList = data.list;
                this.total = res.data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            });
        },
        pageChange (index) {
            this.tableFrom.page = index;
            this.getList();
        },
        changeType (data) {
            this.type = data;
        },
        // 添加
        add () {
            // this.$modalForm(couponCreateApi()).then(() => this.getList());
            this.addType(0);
        },
        addType (type) {
            couponCreateApi(type).then(async res => {
                if (res.data.status === false) {
                    return this.$authLapse(res.data);
                }
                this.FromData = res.data;
                this.$refs.edits.modals = true;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 编辑
        edit (row) {
            this.$modalForm(couponEditeApi(row.id)).then(() => this.getList());
        },
        // 表格搜索
        userSearchs () {
            this.tableFrom.page = 1;
            this.getList();
        },
        // 修改成功
        submitFail () {
            this.getList();
        }
    }
}
