import { formatDate } from '@/utils/validate';
import { mapState } from 'vuex';
import { wechatActionListApi } from '@/api/app';
export default {
    name: 'message',
    filters: {
        formatDate (time) {
            if (time !== 0) {
                let date = new Date(time * 1000);
                return formatDate(date, 'yyyy-MM-dd hh:mm');
            }
        }
    },
    data () {
        return {
            timeVal: [],
            fromList: {
                title: '选择时间',
                custom: true,
                fromTxt: [
                    { text: '全部', val: '' },
                    { text: '今天', val: 'today' },
                    { text: '昨天', val: 'yesterday' },
                    { text: '最近7天', val: 'lately7' },
                    { text: '最近30天', val: 'lately30' },
                    { text: '本月', val: 'month' },
                    { text: '本年', val: 'year' }
                ]
            },
            formValidate: {
                limit: 15,
                page: 1,
                nickname: '',
                data: '',
                type: ''
            },
            loading: false,
            tabList: [],
            total: 0,
            columns4: [
                {
                    title: 'ID',
                    width: 80,
                    key: 'id'
                },
                {
                    title: '操作用户',
                    key: 'nickname',
                    minWidth: 120
                },
                {
                    title: '操作名称',
                    key: 'type_name',
                    minWidth: 120
                },
                {
                    title: '关联内容',
                    key: 'headimgurl',
                    minWidth: 150
                },
                {
                    title: '操作时间',
                    slot: 'add_time',
                    minWidth: 150
                }
            ]
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        ...mapState('admin/order', [
            'orderChartType'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 80;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    created () {
        this.getList();
    },
    methods: {
        // 具体日期
        onchangeTime (e) {
            this.timeVal = e;
            this.formValidate.data = this.timeVal.join('-');
            this.getList();
        },
        // 选择时间
        selectChange (tab) {
            this.formValidate.data = tab;
            this.timeVal = [];
            this.getList();
        },
        // 列表
        getList () {
            this.loading = true;
            this.formValidate.type = this.formValidate.type ? this.formValidate.type : '';
            wechatActionListApi(this.formValidate).then(async res => {
                let data = res.data
                this.tabList = data.list;
                this.total = data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index;
            this.getList();
        },
        // 搜索
        userSearchs () {
            this.getList();
        },
        timeChange () {},
        Refresh () {}
    }
}
