import { mapState } from "vuex";
import {
  releasedListApi,
  releasedissueLogApi,
  releaseStatusApi,
  delCouponReleased,
  couponStatusApi
} from "@/api/marketing";
import { formatDate } from "@/utils/validate";
export default {
  name: "storeCouponIssue",
  filters: {
    formatDate(time) {
      if (time !== 0) {
        let date = new Date(time * 1000);
        return formatDate(date, "yyyy-MM-dd hh:mm");
      }
    }
  },
  data() {
    return {
      modals2: false,
      grid: {
        xl: 7,
        lg: 7,
        md: 12,
        sm: 24,
        xs: 24
      },
      loading: false,
      columns1: [
        {
          title: "ID",
          key: "id",
          width: 80
        },
        {
          title: "优惠券名称",
          slot: "coupon_title",
          minWidth: 150
          // render (h, data) {
          //     let row = data.row, content = '';
          //     if (row.is_give_subscribe) {
          //         content = '关注';
          //     } else if (row.is_full_give) {
          //         content = '满赠';
          //     } else {
          //         content = '普通'
          //     }
          //     return h('div', [
          //         h('Tag', { attrs: {
          //             color: 'blue'
          //         } }, content),
          //         h('span', data.row.coupon_title)
          //     ]);
          // }
        },
        {
          title: "优惠券类型",
          slot: "coupon_type",
          minWidth: 80
        },
        {
          title: "适用类型",
          slot: "type",
          minWidth: 80
        },
        {
          title: "面值",
          slot: "coupon_price",
          minWidth: 100
        },
        {
          title: "领取方式",
          slot: "receive_type",
          minWidth: 100
        },
        {
          title: "领取日期",
          slot: "start_time",
          minWidth: 250
        },
        {
          title: "使用时间",
          slot: "start_use_time",
          minWidth: 250
        },
        {
          title: "发布数量",
          slot: "count",
          minWidth: 90
        },
        {
          title: "是否开启",
          slot: "status",
          minWidth: 90
        },
        {
          title: "操作",
          slot: "action",
          fixed: "right",
          minWidth: 200
        }
      ],
      tableFrom: {
        status: "",
        coupon_title: "",
        receive_type: "",
        coupon_type: "",
        page: 1,
        limit: 15
      },
      receive_type: "",
      tableList: [],
      total: 0,
      FromData: null,
      receiveList: [],
      loading2: false,
      columns2: [
        {
          title: "ID",
          key: "uid",
          minWidth: 80
        },
        {
          title: "用户名",
          key: "nickname",
          minWidth: 150
        },
        {
          title: "用户头像",
          slot: "avatar",
          minWidth: 100
        },
        {
          title: "领取时间",
          key: "add_time",
          minWidth: 140
        }
      ],
      total2: 0,
      receiveFrom: {
        page: 1,
        limit: 15
      },
      rows: {}
    };
  },
  created() {
    this.getList();
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    }
  },
  methods: {
    // 失效
    couponInvalid(row, tit, num) {
      this.delfromData = {
        title: tit,
        num: num,
        url: `marketing/coupon/status/${row.id}`,
        method: "PUT",
        ids: ""
      };
      this.$refs.modelSure.modals = true;
    },
    // 领取记录
    receive(row) {
      this.modals2 = true;
      this.rows = row;
      this.getReceivelist(row);
    },
    getReceivelist(row) {
      this.loading2 = true;
      releasedissueLogApi(row.id, this.receiveFrom)
        .then(async res => {
          let data = res.data;
          this.receiveList = data.list;
          this.total2 = res.data.count;
          this.loading2 = false;
        })
        .catch(res => {
          this.loading2 = false;
          this.$Message.error(res.msg);
        });
    },
    // 领取记录改变分页
    receivePageChange(index) {
      this.receiveFrom.page = index;
      this.getReceivelist(this.rows);
    },
    // 删除
    couponDel(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `marketing/coupon/released/${row.id}`,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then(res => {
          this.$Message.success(res.msg);
          this.getList();
        })
        .catch(res => {
          this.$Message.error(res.msg);
        });
    },
    // 列表
    getList() {
      this.loading = true;
      this.tableFrom.receive_type =
        this.receive_type === "all" ? "" : this.receive_type;
      this.tableFrom.status = this.tableFrom.status || "";
      releasedListApi(this.tableFrom)
        .then(async res => {
          let data = res.data;
          this.tableList = data.list;
          this.total = res.data.count;
          this.loading = false;
        })
        .catch(res => {
          this.loading = false;
          this.$Message.error(res.msg);
        });
    },
    pageChange(index) {
      this.tableFrom.page = index;
      this.getList();
    },
    // 表格搜索
    userSearchs() {
      this.tableFrom.page = 1;
      this.getList();
    },
    // 搜索()
    orderSearch () {
      this.tableFrom.page = 1;
      this.getList();
    },
    // 添加优惠券
    add() {
      this.$router.push({ path: "/admin/marketing/store_coupon_issue/create" });
    },
    // 复制
    copy(data) {
      this.$router.push({
        path: `/admin/marketing/store_coupon_issue/create/${data.id}`
      });
    },
    // 是否开启
    openChange(data) {
      couponStatusApi(data).then(() => this.getList());
    }
  }
};
