import util from "@/libs/util";
import Setting from "@/setting";
import addStore from "../components/addStore";
import {
  storeListApi,
  storeLogin,
  storeSetShowApi,
  resetApi,
} from "@/api/store";
export default {
  name: "storeList",
  components: {
    addStore,
  },
  data() {
    return {
      BaseURL: Setting.apiBaseURL.replace(/adminapi/, "store/home/"),
      total: 0,
      loading: false,
      formValidate: {
        type: "all",
        page: 1,
        limit: 15,
      },
      columns: [
        {
          title: "ID",
          key: "id",
          width: 60,
        },
        {
          title: "门店图片",
          slot: "image",
          minWidth: 80,
        },
        {
          title: "门店名称",
          key: "name",
          minWidth: 80,
        },
        {
          title: "联系电话",
          key: "phone",
          minWidth: 90,
        },
        {
          title: "门店地址",
          key: "address",
          ellipsis: true,
          minWidth: 150,
        },
        {
          title: "营业时间",
          key: "day_time",
          minWidth: 120,
        },
        {
          title: "营业状态",
          key: "status_name",
          minWidth: 80,
        },
        {
          title: "操作",
          slot: "action",
          fixed: "right",
          minWidth: 240,
          align: "center",
        },
      ],
      orderList: [
        {
          id: "1",
          order_id: "12",
          pay_price: "12",
          status: 1,
          phone: "13000000000",
          address: "陕西省西安市莲湖区大兴西路啊餐厨",
        },
      ],
    };
  },
  mounted() {
    this.getList();
  },
  methods: {
    getList() {
      this.loading = true;
      storeListApi(this.formValidate).then((res) => {
        this.orderList = res.data.list;
        this.total = res.data.count;
        this.loading = false;
      });
    },
    reset(row) {
      this.$modalForm(resetApi(row.id)).then(() => this.getList());
    },
    add() {
      this.$refs.template.title = "添加门店";
      this.$refs.template.add = 1;
      this.$refs.template.isTemplate = true;
      this.$refs.template.cityInfo({ pid: 0 });
    },
    edit(row) {
      this.$refs.template.title = "编辑门店";
      this.$refs.template.isTemplate = true;
      this.$refs.template.getInfo(row.id);
    },
    getExpiresTime(expiresTime) {
      let nowTimeNum = Math.round(new Date() / 1000);
      let expiresTimeNum = expiresTime - nowTimeNum;
      return parseFloat(parseFloat(parseFloat(expiresTimeNum / 60) / 60) / 24);
    },
    // 进入门店
    gostore(item) {
      storeLogin(item.id)
        .then((res) => {
          let data = res.data;
          let expires = data.expires_time;
          util.cookies.setStore("token", data.token, {
            expires: expires,
          });
          util.cookies.setStore("uuid", data.user_info.id, {
            expires: expires,
          });
          util.cookies.setStore("expires_time", expires, {
            expires: expires,
          });
          let storage = window.localStorage;
          storage.setItem("menuListStore", JSON.stringify(data.menus));
          storage.setItem("uniqueAuthStore", JSON.stringify(data.unique_auth));
          let userInfoStore = {
            account: data.user_info.account,
            head_pic: data.user_info.avatar,
            logo: data.logo,
            logoSmall: data.logo_square,
            version: data.version,
          };
          storage.setItem("userInfoStore", JSON.stringify(userInfoStore));
          // menuListStore
          window.open(this.BaseURL);
        })
        .catch((err) => {
          this.$Message.error(err.msg);
        });
    },
    delte(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `store/store/del/${row.id}`,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          this.getList();
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    operation(row) {
      let a = 0; //修改营业状态的反值
      if (row.is_show == 0) {
        a = 1;
      }
      if (row.is_show == 1) {
        a = 0;
      }
      storeSetShowApi(row.id, a)
        .then((res) => {
          this.getList();
          this.$Message.success(res.msg);
        })
        .catch((err) => {
          this.$Message.error(res.msg);
        });
    },
    searchs() {},
    onClickTab(e) {
      this.formValidate.page = 1;
      this.formValidate.type = e;
      this.getList();
    },
    //分页
    pageChange(status) {
      this.formValidate.page = status;
      this.getList();
    },
  },
};
