import { getOrderRecord } from '@/api/order';
export default {
    name: 'orderRecord',
    data () {
        return {
            modals: false,
            loading: false,
            recordData: [],
            page: {
                page: 1, // 当前页
                limit: 10 // 每页显示条数
            },
            columns: [
                {
                    title: '订单ID',
                    key: 'oid',
                    align: 'center',
                    minWidth: 40
                },
                {
                    title: '操作记录',
                    key: 'change_message',
                    align: 'center',
                    minWidth: 280
                },
                {
                    title: '操作时间',
                    key: 'change_time',
                    align: 'center',
                    minWidth: 100
                }
            ]
        }
    },
    methods: {
        pageChange (index) {
            this.page.pageNum = index
            this.getList();
        },
        getList (id) {
            let data = {
                id: id,
                datas: this.page
            }
            this.loading = true;
            getOrderRecord(data).then(async res => {
                this.recordData = res.data;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        }
    }
}
