result:
.shortVideo{
    .nav{
        width: 100%;
        height: 45px;
        .title{
            font-weight: 600;
            color: #333333;
            font-size: 15px;
        }
        .more{
            font-weight: 400;
            color: #999999;
            font-size: 12px;
            .iconfont{
                font-size: 12px;
            }
        }
    }
    .list{
        padding-bottom: 1px;
        &.on{
            flex-wrap: nowrap;
            overflow: hidden;
            padding-bottom: 15px;
            .item{
                margin-right: 12px;
                margin-bottom: 0;
                .pictrue{
                    margin-right: 0;
                }
            }
        }
        .item{
            margin-bottom: 20px;
            .pictrue{
                width: 113px;
                height: 150px;
                border-radius: 4px;
                position: relative;
                margin-right: 15px;
                img{
                    width: 100%;
                    height: 100%;
                    border-radius: 4px;
                }
                .like{
                    position: absolute;
                    bottom: 4px;
                    left: 7px;
                    font-size: 10px;
                    font-weight: 400;
                    color: #FFFFFF;
                    .iconfont{
                        font-size: 12px;
                        margin-right: 3px;
                    }
                }
            }
            .text{
                flex: 1;
                /*width: 219px;*/
                .goodsList{
                    margin-top: 17px;
                    overflow: hidden;
                    .pictrue{
                        width: 64px;
                        height: 64px;
                        border-radius: 3px;
                        position: relative;
                        margin-right: 13px;
                        &:nth-of-type(3n){
                            margin-right: 0;
                        }
                        .num{
                            position: absolute;
                            top:50%;
                            left:50%;
                            margin-left: -9px;
                            margin-top: -9px;
                            color: #fff;
                            font-size: 15px;
                            font-weight: 400;
                        }
                        img{
                            width: 100%;
                            height: 100%;
                            display: block;
                            border-radius: 3px;
                        }
                        .money{
                            position: absolute;
                            color: #fff;
                            font-size: 11px;
                            bottom: 3px;
                            left:50%;
                            margin-left: -20px;
                        }
                    }
                }
                .conter{
                    height: 67px;
                    .header{
                        .name{
                            flex: 1;
                            width: 150px;
                        }
                        .empty-icon{
                            width: 18px;
                            height: 18px;
                            border-radius: 50%;
                            background-color: #f3f5f7;
                            border: 1px solid #FFFFFF;
                            margin-right: 5px;
                        }
                        img{
                            width: 18px;
                            height: 18px;
                            border: 1px solid #FFFFFF;
                            display: block;
                            margin-right: 5px;
                            border-radius: 50%;
                        }
                        font-weight: 500;
                        color: #333333;
                        font-size: 14px;
                    }
                    .info{
                        font-weight: 400;
                        color: #666666;
                        font-size: 12px;
                        margin-top: 10px;
                    }
                }
            }
        }
    }
}
