import { mapState } from "vuex";
import { videoList } from "@/api/marketing";
export default {
    name: "index",
    data() {
        return {
            formValidate: {
                page: 1,
                limit: 10,
                keyword: ""
            },
            total: 0,
            loading: false,
            grid: {
                xl: 10,
                lg: 10,
                md: 12,
                sm: 24,
                xs: 24,
            },
            tableList: [],
            currentid: 0,
            videoRow: {},
            columns: [
                {
                    title: "ID",
                    key: "id",
                    width: 80,
                },
                {
                    title: "视频",
                    slot: "image",
                    minWidth: 90,
                },
                {
                    title: "视频介绍",
                    slot: "desc",
                    minWidth: 150,
                }
            ],
            images: [],
        };
    },
    computed: {
        ...mapState("admin/layout", ["isMobile"]),
        labelWidth() {
            return this.isMobile ? undefined : 120;
        },
        labelPosition() {
            return this.isMobile ? "top" : "right";
        },
    },
    created() {
        let radio = {
            width: 60,
            align: "center",
            render: (h, params) => {
                let id = params.row.id;
                let flag = false;
                if (this.currentid === id) {
                    flag = true;
                } else {
                    flag = false;
                }
                let self = this;
                return h("div", [
                    h("Radio", {
                        props: {
                            value: flag,
                        },
                        on: {
                            "on-change": () => {
                                self.currentid = id;
                                this.videoRow = params.row;
                                if (this.videoRow.id) {
                                    if (this.$route.query.fodder === "video") {
                                        /* eslint-disable */
                                        let imageObject = {
                                            image: this.videoRow.image,
                                            video_id: this.videoRow.id,
                                            video_url: this.videoRow.video_url
                                        };
                                        form_create_helper.set("video", imageObject);
                                        form_create_helper.close("video");
                                    }
                                } else {
                                    this.$Message.warning("请先选择商品");
                                }
                            },
                        },
                    }),
                ]);
            },
        };
        this.columns.unshift(radio);
    },
    mounted() {
        this.getList();
    },
    methods: {
        pageChange(index) {
            this.formValidate.page = index;
            this.getList();
        },
        // 列表
        getList() {
            this.loading = true;
            videoList(this.formValidate)
                .then(async (res) => {
                    let data = res.data;
                    this.tableList = data.list;
                    this.total = res.data.count;
                    this.loading = false;
                })
                .catch((res) => {
                    this.loading = false;
                    this.$Message.error(res.msg);
                });
        },
        // 表格搜索
        userSearchs() {
            this.formValidate.page = 1;
            this.getList();
        },
        clear() {
            this.videoRow.id = "";
            this.currentid = "";
        }
    },
};
