let restColor = ''
export default {
    name: 'c_bg_color',
    props: {
        configObj: {
            type: Object
        },
        configNme: {
            type: String
        }
    },
    data () {
        return {
            defaults: {
            },
            configData: {},
            bgColor: {
                bgStar: '',
                bgEnd: ''
            },
            oldColor: {
                bgStar: '',
                bgEnd: ''
            },
            index: 0
        }
    },
    created () {
        this.defaults = this.configObj
        this.configData = this.configObj[this.configNme]
    },
    watch: {
        configObj: {
            handler (nVal, oVal) {
                this.defaults = nVal
                this.configData = nVal[this.configNme]
            },
            immediate: true,
            deep: true
        }
    },
    methods: {
        changeColor (e, color) {
            if (!e) {
                color.item = 'transparent'
            }
            // this.$emit('getConfig', this.defaults)
        },
        // 重置
        resetBgA (color, index, key) {
            color.item = this.configData.default[key].item
        }
    }
}
