import { orderRemark, refundRemarkData } from "@/api/kefu";
export default {
  name: "remarks",
  props: {
    remarkId: {
      type: String,
      default: "",
    },
    itemId: {
      type: Number,
      default: 0,
    },
    remarkTitle: {
      type: String,
      default: "",
    },
    remarkType: {
      default: 1,
      type: Number,
    },
  },
  data() {
    return {
      formValidate: {
        con: "",
      },
      ruleInline: {
        con: [{ required: true, message: "请输入备注信息", trigger: "change" }],
      },
      formValidate: {
        con: "",
      },
    };
  },
  mounted() {
    this.getremark();
  },
  methods: {
    getremark() {
      this.formValidate.con = this.remarkTitle;
    },
    handleSubmit(name) {
      this.$refs[name].validate((valid) => {
        if (valid) {
          (this.remarkType == -1 ? refundRemarkData : orderRemark)({
            order_id: this.remarkId,
            id: this.itemId,
            remark: this.formValidate.con,
          })
            .then((res) => {
              this.$Message.success(res.msg);
              this.$emit("remarkSuccess");
            })
            .catch((error) => {
              this.$Message.error(error.msg);
            });
        } else {
        }
      });
    },
    close() {
      this.$emit("close");
    },
  },
};
